-- ----------------------------
DROP PROCEDURE IF EXISTS `ActualizaCotizacionEstadoVigente`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ActualizaCotizacionEstadoVigente`(codCoti int(11), codDetalle int(11))
BEGIN

   DECLARE codTran INT(11); 
   DECLARE bandp INT(11);
   DECLARE band INT(11);
   DECLARE fecha datetime;
   DECLARE cont INT(11);
   DECLARE total INT(11);
       
   SET bandp = (SELECT cantidadpendiente FROM detallecotizacion WHERE codDetalleCotizacion = codDetalle);
   SET band = (SELECT cantidad FROM detallecotizacion WHERE codDetalleCotizacion = codDetalle);
   SET fecha = (SELECT fechavigencia FROM cotizacion WHERE codCotizacion = codCoti);

   IF bandp = 0 THEN
      UPDATE detallecotizacion SET vigente=4 WHERE codDetalleCotizacion=codDetalle; 
   END IF;
  
   IF(bandp < band) THEN 
      UPDATE detallecotizacion SET vigente=3 WHERE codDetalleCotizacion=codDetalle;
   END IF;

   IF fecha < NOW() THEN
      UPDATE cotizacion SET vigente=0 WHERE codCotizacion=codCoti;
      UPDATE detallecotizacion SET vigente=0 WHERE codCotizacion=codCoti;
   END IF;

   SET total = (SELECT COUNT(*) FROM detallecotizacion);
   SET cont = (SELECT COUNT(*) FROM detallecotizacion WHERE vigente=4);

   IF total = cont THEN
      UPDATE cotizacion SET vigente=4 WHERE codCotizacion=codCoti;
   ELSE
      UPDATE cotizacion SET vigente=3 WHERE codCotizacion=codCoti;
   END IF;

END
;;
DELIMITER ;

