declare sal decimal(10,3);

set sal= (select SUM(ingreso) - SUM(egreso)  from ctactemovimientos where codCuentaCorriente = codcuen and estado = 1);

#1 soles , 2 dolares
#0 egresos , 1 ingresos

if (tipo = 0) THEN
     
UPDATE ctactemovimientos 
SET descripcion=descrip, 
        egreso = ingresoegreso,
        saldo = (sal+ingresoegreso),
				fechaMovimiento = fechamov, 
				codCuentaCorriente = codcuen,
				moneda = mone,
				codUser = codu
WHERE codCtaCteMovimiento= codmovi AND codAlmacen=codalma;

elseif(tipo = 1) THEN

UPDATE ctactemovimientos 
SET descripcion=descrip, 
        ingreso = ingresoegreso,
				saldo = (sal+ingresoegreso), 
				fechaMovimiento = fechamov, 
				codCuentaCorriente = codcuen,
				moneda = mone,
				codUser = codu
WHERE codCtaCteMovimiento= codmovi AND codAlmacen=codalma;
end IF;

SET newid = LAST_INSERT_ID();
END
;;
