BEGIN
UPDATE detalleorden_compra
SET cantidadPendiente=cantidadPendiente-cant
where codProducto=codpro and codOrdenCompra=codord AND codDetalleOrden=coddeta; 

SET @var= (select count(*)
from detalleorden_compra dt 
LEFT OUTER JOIN producto p on p.codProducto=dt.codProducto
LEFT OUTER JOIN unidadmedida um on um.codUnidadMedida=dt.unidad
where dt.codOrdenCompra=codord  and dt.vigente=1 and dt.anulado=0 and dt.cantidadPendiente > 0
GROUP BY p.codProducto LIMIT 1);


if ( @var <> NULL or @var <> 0 )
THEN
UPDATE orden_compra SET vigente =3 WHERE codOrdenCompra=codord;
ELSE
UPDATE orden_compra SET vigente =2 WHERE codOrdenCompra=codord;
END IF;

END
;;
