DROP PROCEDURE IF EXISTS `ActualizaPendienteCredito`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ActualizaPendienteCredito`(monto decimal(10,4), codnota int(11), codalm int(11), tipo int(11))
BEGIN
	/* (1) VENTAS, (2) COMPRAS*/
	DECLARE pendientNC DECIMAL(10, 4) DEFAULT 0;
	IF tipo = 1 THEN		
		SELECT pendiente INTO pendientNC FROM notaingreso WHERE codNotaIngreso = codnota AND estado = 1 AND codAlmacen = codalm;
		IF  monto <= pendientNC THEN
			SET pendientNC = pendientNC - monto;
			UPDATE notaingreso SET pendiente = pendientNC WHERE codNotaIngreso = codnota;
		END IF;
	ELSE
		SELECT pendiente INTO pendientNC FROM notasalida WHERE codNotaSalida = codnota AND estado = 1 AND codAlmacen = codalm;
		IF  monto <= pendientNC THEN
			SET pendientNC = pendientNC - monto;
			UPDATE notasalida SET pendiente = pendientNC WHERE codNotaSalida = codnota;
		END IF;
	END IF;
END
;;
DELIMITER ;

