BEGIN
	#Routine body goes here...
/* consulta jes
UPDATE unidadequivalente
set
Precio =p
where codUnidadEquivalente=cod;
*/

-- consulta bochi
DECLARE tc DECIMAL(11,6);

SET tc = (SELECT tipocambio.venta
					FROM tipocambio
					WHERE DATE_FORMAT(tipocambio.fecharegistro,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d')
					ORDER BY tipocambio.venta DESC LIMIT 1); 

IF(SELECT codMoneda FROM unidadequivalente WHERE codUnidadEquivalente = cod) = 1 THEN
	UPDATE unidadequivalente
	SET Precio = p
	WHERE codUnidadEquivalente=cod;
ELSE
	UPDATE unidadequivalente
	SET PrecioFinal = p,
	    Precio = p*tc
	WHERE codUnidadEquivalente=cod;
END IF;
END
;;
DELIMITER ;

