-- ----------------------------
-- Procedure structure for ActualizaVigenciadeOrdenes
-- ----------------------------
DROP PROCEDURE IF EXISTS `ActualizaVigenciadeOrdenes`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ActualizaVigenciadeOrdenes`(codord int(11))
BEGIN
DECLARE v_finished 		INTEGER DEFAULT 0;
DECLARE v_codDetalleOrden		INTEGER 	DEFAULT 0;
DECLARE v_cantidadPendiente		DOUBLE 	DEFAULT 0;
DECLARE v_codProducto		INTEGER 	DEFAULT 0;
DECLARE v_codAlmacen	INTEGER 	DEFAULT 0;


DECLARE lista_cursor CURSOR FOR 
SELECT codDetalleOrden, cantidadPendiente, codProducto,codAlmacen 
FROM detalleorden_compra 
WHERE anulado=0 and estado=1 and codOrdenCompra=codord;

DECLARE CONTINUE HANDLER 
FOR NOT FOUND SET v_finished = 1;



OPEN lista_cursor;
update_listas: LOOP
		 
FETCH lista_cursor INTO v_codDetalleOrden,v_cantidadPendiente,v_codProducto,v_codAlmacen;

IF v_cantidadPendiente=0
THEN
UPDATE detalleorden_compra SET vigente =2 
WHERE codDetalleOrden=v_codDetalleOrden;
ELSE
UPDATE detalleorden_compra SET vigente =3 
WHERE codDetalleOrden=v_codDetalleOrden;
END IF;


IF v_finished = 1 THEN 
								LEAVE update_listas;
						END IF;


END LOOP update_listas;
CLOSE lista_cursor;



END
;;
DELIMITER ;

