DROP PROCEDURE IF EXISTS `AlertaFacturasLetrasVencidas`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `AlertaFacturasLetrasVencidas`()
BEGIN
	(SELECT fa.DocumentoFactura as Documento, pro.razonsocial as proveedor, DATE(fa.fechapago) as fechavence, fa.total as total, fa.pendiente as pendiente
	FROM facturacion fa
	INNER JOIN proveedor pro ON fa.codProveedor = pro.codProveedor
  WHERE fa.cancelado = 0 AND fa.fechapago BETWEEN DATE(NOW()) AND DATE_ADD(DATE(NOW()),INTERVAL 3 DAY))

	UNION ALL

	(SELECT CONCAT("LT-",le.numdocumento) as Documento, pro.razonsocial as proveedor, DATE(le.fechavencimiento) as fechavence, le.monto as total, le.montopendiente as pendiente
	FROM letra le
	INNER JOIN facturacion fa ON le.codNota = fa.codFactura
	INNER JOIN proveedor pro ON fa.codProveedor = pro.codProveedor
	WHERE le.cancelado = 0 AND le.fechavencimiento BETWEEN DATE(NOW()) AND DATE_ADD(DATE(NOW()),INTERVAL 3 DAY));

END
;;
DELIMITER ;

