-- ----------------------------
-- Procedure structure for BuscaDetalleOrdenCompra
-- ----------------------------
DROP PROCEDURE IF EXISTS `BuscaDetalleOrdenCompra`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `BuscaDetalleOrdenCompra`(codOrd  int(11))
BEGIN

SELECT  dt.codDetalleOrden as codDetalle, dt.codProducto, p.referencia, p.descripcion as producto,
u.codUnidadMedida,CONCAT(dt.moneda) AS moneda,u.descripcion as unidad,dt.serielote,
 IFNULL((select stockactual from productoalmacen where codAlmacen=oc.codAlmacen and codProducto=p.codProducto  GROUP BY codProducto ) ,0) as StockActual
 , dt.cantidad,dt.cantidad as cantidadpendiente ,dt.preciounitario, dt.subtotal, dt.descuento1,dt.descuento2,dt.descuento3,dt.montodscto,
(dt.importe-dt.igv) as valorventa, 0.00 as vvconflete,dt.igv,dt.flete, dt.importe ,0.00 as pvconflete, dt.precioreal, dt.valoreal,dt.fecharegistro,
CONCAT(dt.codUser) AS codUser, 
IFNULL((select stockpendiente from productoalmacen where codAlmacen=oc.codAlmacen and codProducto=p.codProducto  GROUP BY codProducto ),0) as PorLlegar,
 IFNULL((select sum(cantidad) from detallerequerimiento where cantidad>cantidaddespachada and estado=1 and anulado=0 and vigente=1 and codProducto=p.codProducto and CodOrdenCompra is not null GROUP BY codProducto),0) as PorAtender,
IFNULL((( IFNULL((select stockpendiente from productoalmacen where codAlmacen=oc.codAlmacen  and codProducto=p.codProducto  GROUP BY codProducto ),0)+
 IFNULL((select stockactual from productoalmacen where codAlmacen=oc.codAlmacen and codProducto=p.codProducto  GROUP BY codProducto ) ,0))
-(dt.cantidad+
 IFNULL((select sum(cantidad) from detallerequerimiento where cantidad>cantidaddespachada and estado=1 and anulado=0 and vigente=1 and codProducto=p.codProducto and CodOrdenCompra is not null  GROUP BY codProducto),0))
),0) as PorCompletar
FROM 		detalleorden_compra dt INNER JOIN producto p ON dt.codProducto = p.codProducto
INNER JOIN unidadmedida u ON dt.unidad = u.codUnidadMedida
INNER JOIN orden_compra oc on oc.codOrdenCompra=CodOrd
WHERE 	dt.codOrdenCompra =oc.codOrdenCompra AND dt.codOrdenCompra=codOrd and dt.anulado=0 and dt.estado=1
ORDER BY dt.codDetalleOrden;
END
;;
DELIMITER ;

