oc.fechaorden as fecha,
CONCAT(u.nombre," ",u.apellido) as responsable, oc.comentario, CONCAT(s.serie," - ",oc.numeracion) as documento, 
(SELECT CASE oc.anulado when 0 then "No" when 1 Then "Si" END) as Estado, "" as flete,(SELECT CASE oc.vigente 
	when 0 then "BORRADOR" 
	when 1 Then "ENVIADO" 
	when 2 Then "ATENDIDO" 
	when 3 Then "ATENDIDO PARCIALMENTE" 
	when 4 Then "RECHAZADO" 
	when 5 Then "TERMINADO" 
END) as Atendido, oc.codAlmacen as almacen
FROM 		orden_compra oc
INNER JOIN usuario u on u.codUsuario = oc.codUsuario
LEFT OUTER JOIN proveedor pr on oc.codProveedor = pr.codProveedor
INNER JOIN tipodocumento td on td.codTipoDocumento=oc.codTipoDocumento
