, "" as Atendido,"" as Estado,IFNULL(oc.almacendestino,0) as almacendestino ,IFNULL(oc.ComentarioRechazado,'')as ComentarioRechazado
, "" as nombrealmacenDestino
FROM 		requerimiento oc
INNER JOIN usuario u on u.codUsuario = oc.codUsuario
inner join almacen al on al.codAlmacen=oc.codAlmacen
INNER JOIN tipodocumento td on td.codTipoDocumento=oc.codTipoDocumento
LEFT OUTER JOIN serie s on s.codSerie=oc.codserie
LEFT OUTER JOIN detallerequerimiento dt on dt.codRequerimiento=oc.codRequerimiento
WHERE oc.codAlmacen=codOrd  and oc.vigente=0 and oc.anulado=0 and oc.estado=1
and date(oc.fechaorden) BETWEEN fechaini AND fechafin 
ORDER BY  CONCAT(s.serie," - ",oc.numeracion);
END
;;
DELIMITER ;

