         dt.codUser, dt.fecharegistro ,0.00 AS cantingreso,
  IF(dt.cantidad<>(SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen),
    ((SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen) - (SELECT stockporentregar FROM productoalmacen WHERE codProducto=p.codProducto AND codAlmacen=nt.codAlmacen)),dt.cantidad) as cantdespachada,
  IF(dt.cantidad<>(SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen),
    (SELECT stockporentregar FROM productoalmacen WHERE codProducto=p.codProducto AND codAlmacen=nt.codAlmacen),0)as cantpordespachar, 
  IF(dt.cantidad<>(SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen),
    ((SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen) - (SELECT stockporentregar FROM productoalmacen WHERE codProducto=p.codProducto AND codAlmacen=nt.codAlmacen)),dt.cantidad) as cantdespachada2,
  IF(dt.cantidad<>(SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen),
    (SELECT stockporentregar FROM productoalmacen WHERE codProducto=p.codProducto AND codAlmacen=nt.codAlmacen),0)as cantpordespachar2 

FROM detallenotasalida dt LEFT JOIN producto p ON dt.codProducto = p.codProducto
     LEFT JOIN unidadmedida u ON dt.unidadingresada = u.codUnidadMedida
     LEFT JOIN notasalida nt ON dt.codNotaSalida = nt.codNotaSalida
WHERE dt.codNotaSalida = codnota AND p.codProducto = (SELECT codProducto FROM productoalmacen 
                                                      WHERE codAlmacen=dt.codAlmacen AND codProducto=p.codProducto AND 
                                                            stockporentregar>0)
GROUP BY dt.codDetalleSalida
ORDER BY dt.codDetalleSalida ASC;

ELSE 

SELECT dt.codDetalleSalida as codDetalle, dt.codProducto, p.referencia, p.descripcion as producto,u.codUnidadMedida, 
       u.descripcion as unidad, dt.serielote, 
   (SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen)as cantidad, 
       dt.preciounitario, dt.subtotal, dt.descuento1, dt.descuento2, 
       dt.descuento3, dt.montodscto, (dt.importe - dt.igv) as valorventa, dt.igv, dt.importe,dt.valoreal, dt.precioreal, 
       dt.codUser, dt.fecharegistro, /*IF(td.codTipoDocumento <> 1 AND td.codTipoDocumento <> 2, dt.cantidad, 0.00) AS cantingreso,*/ 
  /*IF(td.codTipoDocumento <> 1 AND td.codTipoDocumento <> 2 AND (SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen) = (SELECT sum(ds.cantidad) FROM detallenotasalida ds INNER JOIN notasalida n ON ds.codNotaSalida = n.codNotaSalida
      WHERE ds.codAlmacen=dt.codAlmacen AND ds.codProducto=dt.codProducto AND ds.cantidad <> (SELECT df.cantidad FROM detallefactura_venta df INNER JOIN factura_venta f ON df.codFacturaV=f.codFacturaV
      WHERE df.codProducto=ds.codProducto AND df.codAlmacen=ds.codAlmacen  AND f.codFacturaV = n.documentoreferencia)),
       (SELECT sum(ds.cantidad) FROM detallenotasalida ds INNER JOIN notasalida n ON ds.codNotaSalida = n.codNotaSalida
      WHERE ds.codAlmacen=dt.codAlmacen AND ds.codProducto=dt.codProducto AND 
            ds.cantidad <> (SELECT df.cantidad FROM detallefactura_venta df INNER JOIN factura_venta f ON df.codFacturaV=f.codFacturaV
                            WHERE df.codProducto=ds.codProducto AND df.codAlmacen=ds.codAlmacen  AND f.codFacturaV = n.documentoreferencia)),dt.cantidad)as cantdespachada,*/
   0.00 as cantingreso, 
   if(td.codTipoDocumento <> 1 AND td.codTipoDocumento <> 2,
      ((select cantidad from detallefactura_venta where codAlmacen=dt.codAlmacen and codProducto=p.codProducto and codFacturaV=nt.documentoreferencia)-dt.cantidadpendiente),
       dt.cantidad)as cantdespachada, ifnull(dt.cantidadpendiente,0)as cantpordespachar,
   /*IF(dt.cantidad<>(SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen),
    ((SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen)-dt.cantidad),0)as cantpordespachar,*/
   /*IF(td.codTipoDocumento <> 1 AND td.codTipoDocumento <> 2, 
     ((SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND 
       codAlmacen=dt.codAlmacen)-(SELECT sum(ds.cantidad) FROM detallenotasalida ds INNER JOIN notasalida n ON ds.codNotaSalida = n.codNotaSalida
       WHERE ds.codAlmacen=dt.codAlmacen AND ds.codProducto=dt.codProducto AND 
            ds.cantidad <> (SELECT df.cantidad FROM detallefactura_venta df INNER JOIN factura_venta f ON df.codFacturaV=f.codFacturaV
                           WHERE df.codProducto=ds.codProducto AND df.codAlmacen=ds.codAlmacen  AND f.codFacturaV = n.documentoreferencia))),
    IF(dt.cantidad<>(SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto 
       AND codAlmacen=dt.codAlmacen),((SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen)-dt.cantidad),0))as cantpordespachar,*/ 
  if(td.codTipoDocumento <> 1 AND td.codTipoDocumento <> 2,
     ((select cantidad from detallefactura_venta where codAlmacen=dt.codAlmacen and codProducto=p.codProducto and codFacturaV=nt.documentoreferencia)-dt.cantidadpendiente),
       dt.cantidad)as cantdespachada2, ifnull(dt.cantidadpendiente,0)as cantpordespachar2
  /*IF(dt.cantidad<>(SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen),
    ((SELECT cantidad FROM detallefactura_venta WHERE codFacturaV=nt.documentoreferencia AND codProducto=p.codProducto AND codAlmacen=dt.codAlmacen)-dt.cantidad),0)as cantpordespachar2*/

FROM detallenotasalida dt INNER JOIN producto p ON dt.codProducto = p.codProducto
     INNER JOIN unidadmedida u ON dt.unidadingresada = u.codUnidadMedida
     INNER JOIN notasalida nt ON dt.codNotaSalida = nt.codNotaSalida
     INNER JOIN tipodocumento td ON nt.codTipoDocumento = td.codTipoDocumento
WHERE dt.codNotaSalida = codnota 
GROUP BY dt.codDetalleSalida
ORDER BY dt.codDetalleSalida ASC;

END IF;


