-- ----------------------------
DROP PROCEDURE IF EXISTS `CargaRequerimiento`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `CargaRequerimiento`(codOrd int(11))
BEGIN
SELECT 	oc.codRequerimiento,LPAD(oc.codRequerimiento,11,'0'), al.codAlmacen,al.descripcion as almacen,td.codTipoDocumento,td.sigla,td.descripcion as TipoDocumento, s.codSerie,s.serie,oc.numeracion,
oc.fechaorden, oc.comentario,IFNULL(oc.ComentarioRechazado,''),oc.estado,oc.anulado,CONCAT(u.nombre," ",u.apellido) as responsable,oc.codUsuario, oc.fecharegistro
FROM 		requerimiento oc
INNER JOIN usuario u on u.codUsuario = oc.codUsuario
inner join almacen al on al.codAlmacen=oc.codAlmacen
INNER JOIN tipodocumento td on td.codTipoDocumento=oc.codTipoDocumento
INNER JOIN serie s on s.codSerie=oc.codserie
WHERE 	oc.codRequerimiento = codOrd
ORDER BY oc.codRequerimiento;
END
;;
