-- ----------------------------
-- Procedure structure for cargaRequerimientosTotales
-- ----------------------------
DROP PROCEDURE IF EXISTS `cargaRequerimientosTotales`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `cargaRequerimientosTotales`(alma int(11), almaori int(11))
BEGIN
SELECT dt.codDetalleRequerimiento as codDetalle, dt.codProducto, p.referencia, p.descripcion as producto,
u.codUnidadMedida, u.descripcion as unidad, (dt.cantidad- dt.cantidadDespachada) as cantidad,date(dt.fecharegistro) as fecharequerida,
(select stockactual from productoalmacen where codProducto=dt.codProducto and codAlmacen=1) as stockactual,
 al.descripcion,CONCAT(LEFT(td.descripcion,3),' - ',s.serie,' - ', r.numeracion)as numeracion
FROM detallerequerimiento dt
inner join requerimiento r on r.codRequerimiento=dt.codRequerimiento
INNER JOIN producto p on dt.codProducto= p.codProducto
INNER JOIN unidadmedida u on dt.unidad=u.codUnidadMedida
INNER JOIN almacen al on al.codAlmacen=r.codAlmacen
