-- ----------------------------
-- Procedure structure for CuotasPrestamo
-- ----------------------------
DROP PROCEDURE IF EXISTS `CuotasPrestamo`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `CuotasPrestamo`(codpre INT(11))
BEGIN
	#Routine body goes here...
SELECT 
pb.codPrestamoBancario, pb.CodBanco, b.descripcion AS descBanco, pb.CodMoneda, m.descripcion AS descMoneda,  
pb.tipoCambio, pb.montoprestamo, pb.montointeres, pb.montodevolver, pb.pendiente, pb.montomora, pb.fechaaprobacion, pb.fechavencimiento, 
pb.descripcion, (CASE pb.cronograma WHEN 0 THEN "NO" WHEN 1 THEN "SI" END) AS crono, 
pb.cancelado, IFNULL(pb.fechacancelado,"") AS fechacancelado,
(CASE pb.cancelado WHEN 0 THEN "PENDIENTE" WHEN 1 THEN "CANCELADO" END) AS esta,
(SELECT COUNT(cp.codPrestamoBancario) FROM cuotaprestamo cp WHERE pb.codPrestamoBancario = cp.codPrestamoBancario AND cp.estado = 1) as cantcuotas,

cu.codCuotaPrestamo, cu.nroCuota, cu.fechavencimiento as cuoVence, cu.monto as cuoTotal, cu.montopendiente as cuoPendiente, 
cu.montoadicional, (CASE cu.cancelado WHEN 0 THEN "PENDIENTE" WHEN 1 THEN "Cancelado" END) AS cuoCancelado, cu.fechacancelado,

p.codPago, p.fechapago,(SELECT CASE p.codMoneda WHEN 1 THEN "SOLES" WHEN 2 THEN "DOLARES" END) AS moneda, p.montopagado, p.mora,
mp.descripcion as tipo, p.noperacion, p.ncheque

FROM cuotaprestamo cu LEFT OUTER JOIN prestamobancario pb ON pb.codPrestamoBancario = cu.codPrestamoBancario
LEFT OUTER JOIN banco b ON pb.codBanco = b.codBanco	
LEFT OUTER JOIN moneda m on m.codMoneda=pb.codMoneda
LEFT OUTER JOIN pago p ON cu.codCuotaPrestamo=p.codCuotaPreBan
LEFT OUTER JOIN metodopago mp ON p.codTipoPago = mp.codMetodoPago

WHERE cu.estado = 1 AND cu.codPrestamoBancario=codpre
ORDER BY cu.fechavencimiento, cu.codCuotaPrestamo DESC;
END
;;
DELIMITER ;

