-- ----------------------------
-- Procedure structure for FacturasComprasLE
-- ----------------------------
DROP PROCEDURE IF EXISTS `FacturasComprasLE`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `FacturasComprasLE`(mes int(11), alma int(11))
BEGIN
	#Routine body goes here...

SELECT 
	DATE_FORMAT(NOW(),'%Y%m%00') as Periodo,
	LPAD(fac.codFactura,3,0) as CUO, 
	CONCAT("M - ",LPAD(fac.codFactura,3,0)) as Estado_CUO,
	DATE_FORMAT(fac.fecharegistro,'%d/%m/%Y') as Fecha_Emision,
	(CASE tipdoc.codsunat WHEN 14 THEN DATE_FORMAT(fac.fechapago,'%d/%m/%Y')  WHEN 01 THEN DATE_FORMAT(fac.fechapago,'%d/%m/%Y') 
												WHEN 03 THEN DATE_FORMAT(fac.fechapago,'%d/%m/%Y')  WHEN 12 THEN DATE_FORMAT(fac.fechapago,'%d/%m/%Y') END) as  Fecha_Vencimiento,
	(tipdoc.codsunat) as TipComprobante,
	LPAD(SUBSTRING_INDEX(SUBSTRING_INDEX(fac.DocumentoFactura,"-",2),"-",-1),4,0) as serie,
	("") as Anio_Emision,
	SUBSTRING_INDEX(SUBSTRING_INDEX(SUBSTRING_INDEX(fac.DocumentoFactura,"-",3),"-",-2),"-",-1) as Nro_Comprobante,
	(0) as Consolidacion,
	("") as TipDoc_Proveedor,
	(CASE tipdoc.codsunat WHEN 01 THEN pr.ruc WHEN 02 THEN pr.ruc WHEN 04 THEN pr.ruc  WHEN 09 THEN pr.ruc END) as RUC,
	(CASE tipdoc.codsunat WHEN 01 THEN pr.razonsocial WHEN 02 THEN pr.razonsocial WHEN 04 THEN pr.razonsocial  WHEN 09 THEN pr.razonsocial END) as Razon_Social,
	IF(fac.tipofacturacion = 1, ROUND(fac.total - fac.igv,2), 0) as Base_Imponible1,
	IF(fac.tipofacturacion = 1, ROUND(fac.igv,2), 0) as IGV1,
	IF(fac.tipofacturacion = 2, ROUND(fac.total - fac.igv,2), 0) as Base_Imponible2,
	IF(fac.tipofacturacion = 2, ROUND(fac.igv,2), 0) as IGV2,
	IF(fac.tipofacturacion = 3, ROUND(fac.total - fac.igv,2), 0) as Base_Imponible3,
	IF(fac.tipofacturacion = 3, ROUND(fac.igv,2), 0) as IGV3,
	(0) Valor_Adqui_ng,
	(0) as Monto_Impuesto_Selec,
	(0) Otros_Conceptos,
	(fac.total) as Tot_Total,
	(mon.codsunat) as Moneda, 
	(fac.tipocambio) as Tipo_Cambio,
	(CASE tipdoc.codsunat WHEN 07 THEN DATE(fac.fechapago) WHEN 08 THEN DATE(fac.fechapago) 
												WHEN 87 THEN DATE(fac.fechapago) WHEN 88 THEN DATE(fac.fechapago)
												WHEN 97 THEN DATE(fac.fechapago) WHEN 98 THEN DATE(fac.fechapago) END) as Fecha_Comprobante,
	(0) as Tip_Compro_Modi,
	(0) as Num_Ser_Compr_modi,
	(0) as cod_aduana,
	(0) as 	num_comprob_modi,
	("") as fecha_constan_detrac,
	("") as num_constan_detrac,
	(0) as marca_comprob_pago,
	(0) as clasif_bien_serv,("") as ident_proyecto,
	(0)	as Inconsistencia_TipoCambio,
	(0) as Inconsistencia_TipoProveedorHabidos,
	(0) as Inconsistencia_PtoveedorRenuncia,
	(0) as Inconsistencia_PorDNI,
	IF(pag.codsunat,1,0) as Medio_Pago,
	(1) as Estado

FROM 	facturacion fac
	INNER JOIN usuario u on u.codUsuario = fac.codUsuario
	LEFT JOIN tipodocumento tipdoc ON tipdoc.codTipoDocumento=fac.codTipoDocumento
	LEFT OUTER JOIN proveedor pr on fac.codProveedor = pr.codProveedor
	LEFT JOIN moneda mon ON mon.codMoneda=fac.moneda
	LEFT JOIN metodopago pag ON pag.codMetodoPago=fac.formapago
WHERE MONTH(fac.fecharegistro)
and fac.codAlmacen = alma
AND fac.codTipoDocumento in (1,2,4,6,7) #1 boleta   2 factura   4 nota de credito  6 nota de debito  7 tickets
ORDER  BY  fac.codFactura; 
/*
SELECT 	Date_format(now(),'%Y%m%00') as periodo, DATE_FORMAT(fac.fechaingreso,'%d/%m/%Y') as f_ingreso ,DATE_FORMAT(fac.fechapago,'%d/%m/%Y') as f_vencimiento , (tipdoc.codsunat) as tip_comp,SUBSTRING(fac.DocumentoFactura,4,3)as serie,
("") as anioemision,SUBSTRING(fac.DocumentoFactura,9) as numdoc,(0) as consolidacion,("") as tipdoc_proveedor,
(SELECT CASE tipdoc.codsunat WHEN 01 THEN pr.ruc WHEN 02 THEN pr.ruc WHEN 04 THEN pr.ruc  WHEN 09 THEN pr.ruc END) as ruc,
(SELECT CASE tipdoc.codsunat WHEN 01 THEN pr.razonsocial WHEN 02 THEN pr.razonsocial WHEN 04 THEN pr.razonsocial  WHEN 09 THEN pr.razonsocial END) as razon_social,
(0) as base_imponible1,ROUND(fac.igv,2)as igv1,(0) as base_imponible2,ROUND(fac.igv,2) as igv2,(0) as base_imponible3,ROUND(fac.igv,2) as igv3,(0) valor_adqui_ng,(0) as monto_impuesto_selec,
(0) otros_conceptos,
(SELECT SUM(detfac.importe)  FROM detallefacturacion detfac WHERE fac.codFactura=detfac.codFactura ) as tot_total,
(mon.codsunat) as moneda,fac.tipocambio,
(SELECT CASE tipdoc.codsunat WHEN 07 THEN DATE(fac.fechacancelado) WHEN 08 THEN DATE(fac.fechacancelado) 
WHEN 87 THEN DATE(fac.fechacancelado) WHEN 88 THEN DATE(fac.fechacancelado)
WHEN 97 THEN DATE(fac.fechacancelado) WHEN 98 THEN DATE(fac.fechacancelado) END) as fecha_comprobante,
(0) as tip_compro_modi,(0) as num_ser_compr_modi,(0) as cod_aduana ,(0) as 	num_comprob_modi,("") as fecha_constan_detrac,
(0) as num_constan_detrac,("") as marca_comprob_pago,("") as clasif_bien_serv,("") as ident_proyecto,
#IF(formapago=codMetodoPago,1,"") as 
IF(pag.codsunat,1,"") as medio_pago,(1) as estado
FROM 		facturacion fac
INNER JOIN usuario u on u.codUsuario = fac.codUsuario
LEFT JOIN tipodocumento tipdoc ON tipdoc.codTipoDocumento=fac.codTipoDocumento
LEFT OUTER JOIN proveedor pr on fac.codProveedor = pr.codProveedor
INNER JOIN moneda mon ON mon.codMoneda=fac.moneda
INNER JOIN metodopago pag ON pag.codMetodoPago=fac.formapago
