SELECT DATE_FORMAT(NOW(),'%Y%m%00') as Periodo,(nota.codNotaIngreso) as CUO, CONCAT("M-",nota.codNotaIngreso) as Estado_CUO,DATE_FORMAT(nota.fechaingreso,'%d/%m/%Y') as Fecha_Emision,

	(tipdoc.codsunat) as TipComprobante,("")as Serie,(nota.numdocumento) as Nro_Comprobante,
	(0) as Valor_Adquisiciones,(0) as Conceptos_Adicionales,(nota.total) as Tot_Total,(0) as Tipo_Comp_Pago,
