DROP PROCEDURE IF EXISTS `GeneraPreciosListaxProducto`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `GeneraPreciosListaxProducto`(codpro int(11), codalma int(11))
BEGIN
#Routine body goes here...
DECLARE v_finished 		INTEGER 	DEFAULT 0;
DECLARE v_codlista		INTEGER 	DEFAULT 0;
DECLARE v_preprom			DOUBLE 		DEFAULT 0;
DECLARE v_margen			DOUBLE 		DEFAULT 0;
DECLARE v_precioneto 	DOUBLE 		DEFAULT 0;
DECLARE v_precio 			DOUBLE 		DEFAULT 0;
DECLARE v_IGV					DOUBLE 		DEFAULT 1;
DECLARE v_origen			INTEGER 	DEFAULT 0;
DECLARE v_dscto1			DOUBLE 		DEFAULT 0;
DECLARE v_dscto2			DOUBLE 		DEFAULT 0;
DECLARE v_dscto3			DOUBLE 		DEFAULT 0;
DECLARE v_decimales		INTEGER		DEFAULT 0;

/*DECLARE v_email varchar(100) DEFAULT "";*/

DECLARE lista_cursor CURSOR FOR 
SELECT codListaPrecio FROM listaprecio WHERE codAlmacen = codalma AND estado = 1 AND updateauto = 1;


DECLARE CONTINUE HANDLER 
FOR NOT FOUND SET v_finished = 1;
		 
OPEN lista_cursor;
update_listas: LOOP
		 
FETCH lista_cursor INTO v_codlista;

SELECT listaorigen, descuento1, descuento2, descuento3, decimales
INTO v_origen, v_dscto1, v_dscto2, v_dscto3, v_decimales 
FROM listaprecio 
WHERE codListaPrecio = v_codlista;
			
			IF v_origen = 0 THEN
								
									SELECT preciopromedio INTO v_preprom FROM productoalmacen WHERE codProducto = codpro AND codAlmacen = codalma ;

									SELECT CASE margenprov WHEN 0 THEN margen WHEN 1 THEN (SELECT pr.margen FROM productosproveedor pp INNER JOIN proveedor pr ON pr.codProveedor = pp.codProveedor WHERE pp.codProducto = codpro HAVING MAX(pp.fecharegistro) ) END INTO v_margen FROM listaprecio WHERE codListaPrecio = v_codlista;
														
									SELECT CASE p.igv WHEN 0 THEN 1 WHEN 1 THEN (SELECT cn.tasaIGV FROM configuracion cn WHERE cn.estado = 1) END INTO v_IGV FROM producto p WHERE p.codProducto = codpro;


									IF v_finished = 1 THEN 
										LEAVE update_listas;
									END IF;

														IF decimales = -1 THEN
																SET v_precioneto = v_preprom * (1+(v_margen/100)) * (1 - (v_dscto1/100)) * (1 - (v_dscto2/100)) * (1 - (v_dscto3/100));
																SET v_precio = v_precioneto * (1+(v_IGV/100));
														ELSE
																SET v_precioneto = ROUND(v_preprom * (1+(v_margen/100)) * (1 - (v_dscto1/100)) * (1 - (v_dscto2/100)) * (1 - (v_dscto3/100)),decimales);
																SET v_precio = ROUND(v_precioneto * (1+(v_IGV/100)),decimales);
														END IF;
															
														-- build email list
														/*SET email_list = CONCAT(v_codpro,";",email_list);*/

															 IF EXISTS(SELECT * FROM detallelistaprecio dl WHERE dl.codProducto = codpro AND dl.codListaPrecio = codlista)
															 THEN
																		UPDATE 	detallelistaprecio
																		SET    	valor = v_preprom,
																						margen = v_margen,
																						descuento1 = v_dscto1,
																						descuento2 = v_dscto2,
																						descuento3 = v_dscto3,
																						precioneto = v_precioneto,
																						precio = v_precio
																		WHERE codProducto = codpro AND codListaPrecio = v_codlista;
															 ELSE
																		 INSERT INTO detallelistaprecio (codProducto,codListaPrecio,valor,margen,descuento1,descuento2,descuento3,precioneto,precio) 
																														 VALUES (v_codpro, codlista, v_preprom, v_margen, v_dscto1, v_dscto2, v_dscto3, v_precioneto, v_precio);
															 END IF;	
										 
				

				ELSE	

								SELECT DISTINCT(precioneto) INTO v_preprom FROM detallelistaprecio WHERE codProducto = v_codpro AND codListaPrecio = v_origen;

								SELECT variacion INTO v_margen FROM listaprecio WHERE codListaPrecio = codlista;
										
								SELECT CASE p.igv WHEN 0 THEN 1 WHEN 1 THEN (SELECT cn.tasaIGV FROM configuracion cn WHERE cn.estado = 1) END INTO v_IGV FROM producto p WHERE p.codProducto = codpro;


												IF v_finished = 1 THEN 
														LEAVE update_listas;
												END IF;

												IF decimales = -1 THEN
														SET v_precioneto = v_preprom * (1+(v_margen/100)) * (1 - (v_dscto1/100)) * (1 - (v_dscto2/100)) * (1 - (v_dscto3/100));
														SET v_precio = v_precioneto * (1+(v_IGV/100));
												ELSE
														SET v_precioneto = ROUND(v_preprom * (1+(v_margen/100)) * (1 - (v_dscto1/100)) * (1 - (v_dscto2/100)) * (1 - (v_dscto3/100)),decimales);
														SET v_precio = ROUND(v_precioneto * (1+(v_IGV/100)),decimales);
												END IF;
													
												-- build email list
												/*SET email_list = CONCAT(v_codpro,";",email_list);*/

													 IF EXISTS(SELECT * FROM detallelistaprecio dl WHERE dl.codProducto = codpro AND dl.codListaPrecio = codlista)
													 THEN
																UPDATE 	detallelistaprecio
																SET    	valor = v_preprom,
																				margen = v_margen,
																				descuento1 = v_dscto1,
																				descuento2 = v_dscto2,
																				descuento3 = v_dscto3,
																				precioneto = v_precioneto,
																				precio = v_precio
																WHERE codProducto = codpro AND codListaPrecio = v_codlista;
													 ELSE
																 INSERT INTO detallelistaprecio (codProducto,codListaPrecio,valor,margen,descuento1,descuento2,descuento3,precioneto,precio) 
																												 VALUES (v_codpro, codlista, v_preprom, v_margen, v_dscto1, v_dscto2, v_dscto3, v_precioneto, v_precio);
													 END IF;

				END IF;


END LOOP update_listas;
CLOSE lista_cursor;

END
;;
DELIMITER ;

