CREATE DEFINER=`root`@`localhost` PROCEDURE `GetUltimaCajaVentas`(codsucursal_ex int(11), tipo_ex int(11), codalmacen_ex int(11))
BEGIN

SELECT 	codcaja, montoapertura, DATE(fechaapertura ) AS fechaapertura, 
				montocierre, IFNULL(DATE(fechacierre),DATE(fechaapertura)) AS fechacierre, IF(IFNULL(DATE(fechacierre),'')='',0,1) as estado, codUser, codsucursal
FROM caja 
WHERE codcaja = (SELECT MAX(codcaja) 
								FROM caja  
								WHERE montoApertura > 0 
