-- ----------------------------
-- Procedure structure for GetVentas_Mes_LEV
-- ----------------------------
DROP PROCEDURE IF EXISTS `GetVentas_Mes_LEV`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `GetVentas_Mes_LEV`(mes_ex int(11))
BEGIN
	#Routine body goes here...
	
	SELECT LPAD(fv.codFacturaV,3,'0') AS CUO,
				 DATE(fv.fecharegistro)AS FECHAREGISTRO,
				 DATE(fv.fechapago) AS FECHAPAGO,
				 td.codsunat AS TIPOCOMPROBANTE,
				 LPAD(fv.serie,4,'0') AS serie,
				 LPAD(fv.numDocumento,8,'0') AS numDocumento,
				 (CASE cl.dni WHEN "" THEN cl.ruc WHEN NULL THEN cl.ruc WHEN NOT "" THEN cl.dni WHEN NOT NULL THEN cl.dni END) AS NRODOC,
				 IFNULL(cl.nombre,cl.razonsocial) AS RAZONSOCIAL,
			   ROUND(fv.valorventa,2) AS BASIIMPONIBLE,
				 ROUND(fv.montodscto,2) AS DESCUENTOBI,
				 ROUND(fv.igv,2) AS IGV,
				 ROUND(fv.total,2) AS IMPTOTALOP,
				 md.codsunat AS MONEDA,
				 ROUND(fv.tipocambio,3) AS tipocambio
	FROM factura_venta fv 
	INNER JOIN tipodocumento td ON td.codTipoDocumento = fv.codTipoDocumento
	INNER JOIN cliente cl ON cl.codCliente = fv.codCliente
	INNER JOIN moneda md ON md.codMoneda = fv.moneda
	WHERE MONTH(fv.fecharegistro) = mes_ex;
END
;;
DELIMITER ;

