-- ----------------------------
-- Procedure structure for GuardaCabeceraHistoria
-- ----------------------------
DROP PROCEDURE IF EXISTS `GuardaCabeceraHistoria`;
DELIMITER ;;
CREATE DEFINER=`root`@`%.%.%.%` PROCEDURE `GuardaCabeceraHistoria`(
	IN _numero INT(11),
	IN _pacienteid INT(11),
	IN _usuarioid INT(11),
	IN _fecharegistro DATETIME,
	OUT newid INT(11)
)
BEGIN 
	DECLARE _count INT (11) DEFAULT 0;
	DECLARE _newid INT (11) DEFAULT 0;
	SELECT count(*) INTO _count FROM historia_clinica WHERE numero = _numero;
	IF _count = 0 THEN
		INSERT INTO `historia_clinica` (`pacienteid`, `numero`, `usuario`, `fecharegistro`) 
		VALUES (_pacienteid, _numero, _usuarioid, _fecharegistro);
		SET newid = LAST_INSERT_ID();
	ELSE
		SELECT id into _newid FROM historia_clinica WHERE numero = _numero;
		SET newid = _newid;
	END IF;
END
;;
DELIMITER ;

