BEGIN
DECLARE montoaps DECIMAL(10,4);
DECLARE cant INT;

SELECT COUNT(*) INTO cant FROM flujocaja WHERE codSucursal = sucur and estado = 1;

IF cant > 1 THEN
    SELECT IFNULL(montoDisponible,0.000) INTO montoaps FROM flujocaja WHERE codSucursal = sucur AND estado = 1 ORDER BY fecharegistro desc LIMIT 1,1;
