    -- SELECT IFNULL((SELECT deposito FROM flujocaja WHERE codSucursal = 1 AND estado = 1 ORDER BY fecharegistro desc LIMIT 0,1),0) INTO dep;
    SELECT IFNULL(montoDisponible,0.000) INTO montoaps FROM flujocaja WHERE codSucursal = sucur AND estado = 1 ORDER BY fecharegistro desc LIMIT 0,1;
