-- SELECT IFNULL(MAX(montoDisponible),0.000) INTO montoaps FROM flujocaja WHERE codSucursal=sucur AND estado=1;

  IF (montoaps<>0) THEN
    IF NOT EXISTS(SELECT * FROM flujocaja WHERE codSucursal = sucur and estado = 1 and date(fechaapertura)=date(fec)) THEN 
        INSERT INTO flujocaja(codSucursal,fechaapertura,montoApertura,montoDisponible,fecharegistro,codUser)
				VALUES (sucur,fec,montoaps,montoaps,NOW(),codusu);
        SET newid = LAST_INSERT_ID();
    ELSE 
         SET newid = 0;
    END IF;

  ELSEIF (montoaps=0 or ISNULL(montoaps)) THEN 
    IF NOT EXISTS(SELECT * FROM flujocaja WHERE codSucursal = sucur and estado = 1 and date(fechaapertura)=date(fec)) THEN 
				INSERT INTO flujocaja(codSucursal,fechaapertura,montoApertura,montoDisponible,fecharegistro,codUser)
				VALUES (sucur,fec,montoape,montoape,NOW(),codusu);
