-- ----------------------------
-- Procedure structure for ImprimirCobro
-- ----------------------------
DROP PROCEDURE IF EXISTS `ImprimirCobro`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ImprimirCobro`(codpag int(11), codalma int(11))
BEGIN
SELECT po.razonsocial as cliente, po.ruc as ruc_dni, f.total, f.pendiente,p.montocobrado,
f.abonado as montopagado, DATE_FORMAT(p.fechapago,'%d-%m-%Y') as fechapago, em.razonsocial as razonsocialempre, em.direccion as direccionemp,
em.telefono as telefonoempre, em.ruc, f.abonado as abonado, (f.abonado-p.montocobrado) as saldo, p.tipocambio
, (SELECT m.descripcion FROM moneda m WHERE m.codMoneda = f.moneda) as monfact
, (SELECT m.descripcion FROM moneda m WHERE m.codMoneda = p.codMoneda) as monpa, IFNULL(p.codSerie,0)as codSerie,
IFNULL(p.serie,'')as serie, IFNULL(LPAD(p.numdocumento,4,'0'),"")as numdoc
FROM facturacion f
INNER JOIN pago p ON f.codFactura=p.codNota
INNER JOIN proveedor po ON f.codProveedor=po.codProveedor
INNER JOIN almacen al ON f.codalmacen=al.codAlmacen
INNER JOIN empresa em ON al.codEmpresa=em.codEmpresa
WHERE p.codPago=codpag /*AND al.codAlmacen=codalma*/;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for ImprimirPago
-- ----------------------------
DROP PROCEDURE IF EXISTS `ImprimirPago`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ImprimirPago`(codpag int(11), codalma int(11))
BEGIN

 DECLARE montocob DECIMAL(10,4) DEFAULT 0;
 DECLARE codmon INTEGER DEFAULT 0;
 DECLARE tipoc DECIMAL(10,4) DEFAULT 0;
 DECLARE codmonFact INTEGER DEFAULT 0;

SELECT montocobrado INTO montocob FROM pago WHERE codPago=codpag;
SELECT codMoneda INTO codmon FROM pago WHERE codPago=codpag;
SELECT tipocambio INTO tipoc FROM pago WHERE codPago=codpag;
SELECT moneda INTO codmonFact from factura_venta fv INNER JOIN pago p ON p.codNota=fv.codFacturaV and p.codPago=codpag;

IF(codmonFact<>codmon)
THEN
	IF(codmon=2 AND codmonFact=1)
	THEN
		SET montocob=montocob*tipoc;
  ELSE 
	SET montocob=montocob/tipoc;
	END IF;
END IF;


SELECT cl.razonsocial as cliente, IF(cl.dni='', cl.ruc,cl.dni)as ruc_dni, /*fv.total*/
(SELECT ROUND(IFNULL(SUM(IF(ns.moneda = 2,((ns.pendiente)*ns.tipocambio)+montocob,(ns.pendiente+montocob))),0),2) 
FROM cliente cl1 INNER JOIN factura_venta ns ON ns.codCliente = cl1.codCliente
WHERE cl1.codCliente = cl.codCliente AND ns.cancelado = 0 AND ns.estado = 1 and ns.anulado = 0 and ns.pendiente >= 0.1) as total,
fv.pendiente, p.montocobrado,
     /*p.montopagado*/fv.abonado as montopagado, DATE_FORMAT(p.fechapago,'%d-%m-%Y')as fechapago, IFNULL(ven.nombre,'') AS cobrador,
     em.razonsocial as razonsocialempre, em.direccion as direccionemp, em.telefono as telefonoempre, em.ruc, 
     fv.abonado as abonado,
(SELECT ROUND(IFNULL(SUM(IF(ns.moneda = 2,ns.pendiente*ns.tipocambio,ns.pendiente)),0),2) 
FROM cliente cl1 INNER JOIN factura_venta ns ON ns.codCliente = cl1.codCliente
WHERE cl1.codCliente = cl.codCliente AND ns.cancelado = 0 AND ns.estado = 1 and ns.anulado = 0 and ns.pendiente >= 0.1) as saldo,
 cl.lineacredito, cl.lineacreditodisponible,
     (cl.lineacredito-cl.lineacreditodisponible)AS linea_uso, p.tipocambio
			, (SELECT m.descripcion FROM moneda m WHERE m.codMoneda = fv.moneda) as monfact
			, (SELECT m.descripcion FROM moneda m WHERE m.codMoneda = p.codMoneda) as monpa, IFNULL(p.codSerie,0)as codSerie,
      IFNULL(p.serie,'')as serie, IFNULL(LPAD(p.numdocumento,4,'0'),"")as numdoc,
(SELECT ROUND(IFNULL(SUM(IF(ns.moneda = 2,ns.pendiente*ns.tipocambio,ns.pendiente)),0),2) 
FROM cliente cl1 INNER JOIN factura_venta ns ON ns.codCliente = cl1.codCliente
WHERE cl1.codCliente = cl.codCliente AND ns.cancelado = 0 AND ns.estado = 1 and ns.anulado = 0 and ns.pendiente >= 0.1 and DATE(ns.fechapago)<DATE(now())) as saldovencido,
mon.descripcion as moneda, 
(SELECT ROUND(IFNULL(SUM(IF(ns.moneda = 2,(ns.abonado)*ns.tipocambio,(ns.abonado))),0),2) 
FROM cliente cl1 INNER JOIN factura_venta ns ON ns.codCliente = cl1.codCliente
WHERE cl1.codCliente = cl.codCliente AND ns.cancelado = 0 AND ns.estado = 1 and ns.anulado = 0 and ns.pendiente >= 0.1)as amortizaciontotal
FROM factura_venta fv INNER JOIN pago p ON fv.codFacturaV=p.codNota
     INNER JOIN cliente cl ON cl.codCliente=fv.codCliente
		 INNER JOIN moneda mon ON mon.codMoneda=cl.moneda
     INNER JOIN almacen al ON al.codAlmacen=fv.codAlmacen
     INNER JOIN empresa em ON al.codEmpresa=em.codEmpresa
     LEFT OUTER JOIN vendedor ven ON ven.codVendedor=p.codCobrador
WHERE p.codPago=codpag /*AND al.codAlmacen=codalma*/;
END
;;
DELIMITER ;

