DROP PROCEDURE IF EXISTS `LiquidacionesVigentes`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `LiquidacionesVigentes`(codSucur int(11), tipo int(1))
BEGIN
#Routine body goes here...
SELECT 	r.codRendicion, IFNULL(r.numCheque,'')as numCheque, r.montoLiquidado, r.fechaRendicion, (CASE r.estatus WHEN '1' THEN 
				'VIGENTE' WHEN '2' THEN 'RECHAZADO' WHEN '3' THEN 'APROBADO' WHEN '4' THEN 'LIQUIDADO' END) as estatus, 
				r.fechaliquidacion, IFNULL(r.responsable,'')as responsable

FROM rendicion as r
LEFT JOIN detallerendicion as d ON r.codRendicion = d.codRendicion
INNER JOIN sucursal as s ON s.codSucursal = r.codSucursal
WHERE r.estado = 1 AND r.estatus = 4 AND r.rendido = 1 AND r.codSucursal = codSucur  and r.tipocajachica=tipo

GROUP BY r.codRendicion;

END
;;
DELIMITER ;

