-- ----------------------------
-- Procedure structure for ListaCajaChica
-- ----------------------------
DROP PROCEDURE IF EXISTS `ListaCajaChica`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaCajaChica`(codSucur int(11), fecha1 date, codcaja_ex int(11), codalma_ex int(11))
BEGIN
#Routine body goes here...
SELECT 	cm.codMovCaja, 
					cm.codSucursal, 
					cm.codcaja, 
					IFNULL(cm.codPago,0) as codPago,
					cm.concepto AS concepto, 
					cm.monto, 
					cm.tipo,  # tipo => 2 egreso     1 ingreso
					(CASE cm.tipo WHEN 1 THEN "INGRESO" WHEN 2 THEN "EGRESO" END) AS ingresoegreso,
					cm.tipomovimiento,
					(CASE cm.tipomovimiento WHEN 1 THEN "APERTURA" WHEN 2 THEN "MOVIMIENTO" WHEN 3 THEN "CIERRE" END) AS tipomovimientodescripcion,
					cm.fecha, 
					cm.tipodocumento,
					IFNULL((SELECT sigla FROM tipodocumento WHERE codTipoDocumento = cm.tipodocumento),"") AS documento,
					cm.codSerie, 
					cm.serie, 
					CONCAT(IFNULL((SELECT sigla FROM tipodocumento WHERE codTipoDocumento = cm.tipodocumento),"")," ",cm.serie, " - ",LPAD(cm.NumDocumento,9,0)) AS NumDocumento,
					(CASE cm.tipo
					WHEN 1 THEN IFNULL((SELECT CONCAT((SELECT t.sigla FROM tipodocumento t WHERE t.codTipoDocumento = ns.codTipoDocumento), " ", ns.serie, " - ", LPAD(ns.numdocumento,7,0)) 
											FROM pago p 
											INNER JOIN notasalida ns on ns.codNotaSalida = p.codNota 
											WHERE p.codPago = cm.codPago),"")
					WHEN 2 THEN IFNULL((SELECT CONCAT((SELECT t.sigla FROM tipodocumento t WHERE t.codTipoDocumento = ni.codTipoDocumento), " ", ni.numdocumento) 
											FROM pago p 
											INNER JOIN notaingreso ni on ni.codNotaIngreso = p.codNota 
											WHERE p.codPago = cm.codPago),"")
					END) AS documentorefencia, 
					cm.toneladas, 
					cm.codTipoPagoCaja,
					IFNULL((CASE cm.codTipoPagoCaja WHEN 5 THEN "EFECTIVO" WHEN 6 THEN "DEPOSITO" WHEN 7 THEN "CHEQUE" WHEN 8 THEN "CHEQUE" END),"") AS PAGOCAJA,
					0 AS SALDO,
					cm.nombre,
					cm.dni,
					cm.estado, 
					cm.codUser, 
					cm.fecharegistro,
					cm.codMoneda,
					(CASE cm.codMoneda WHEN 1 THEN "SOLES" WHEN 2 THEN "DOLARES" END) AS moneda,
					cm.tcVenta
	FROM 		caja c
	INNER JOIN cajamovimiento cm ON cm.codcaja = c.codcaja
	WHERE 	c.codsucursal = codSucur 
	AND 		DATE(c.fechaapertura) = DATE(fecha1)
	AND 		c.codcaja = codcaja_ex
	AND			c.codalmacen = codalma_ex;
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for ListaCajaChicaDiaria
-- ----------------------------
DROP PROCEDURE IF EXISTS `ListaCajaChicaDiaria`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaCajaChicaDiaria`(codSucur int(11), fecha1 date, codcaja_ex int(11),codalma int(11))
BEGIN
SELECT 		cm.codMovCaja, #1
					cm.codSucursal,#2 
					cm.codcaja,#3 
					IFNULL(cm.codPago,0) as codPago,#4
					IF(cm.concepto <> '' AND cm.concepto <> NULL, cm.concepto,(CASE cm.tipo									
					WHEN 1 THEN (CASE cm.tipomovimiento WHEN 0 THEN "" WHEN 1 THEN "MONTO APERTURA CAJA" WHEN 2 THEN "MONTO INGRESO - COBRANZA" WHEN 3 THEN "MONTO CIERRE CAJA" WHEN 4 THEN "MONTO DE APORTE" END)
					WHEN 2 THEN "MONTO EGRESO - PAGO" 
					END)) AS concepto, #5
					FORMAT(cm.monto,2), #6
					cm.tipo,  # tipo => 2 egreso     1 ingreso #7
					(CASE cm.tipo WHEN 1 THEN "INGRESO" WHEN 2 THEN "EGRESO" END) AS ingresoegreso, #8
					cm.tipomovimiento, #9
					(CASE cm.tipomovimiento WHEN 1 THEN "APERTURA" WHEN 2 THEN "MOVIMIENTO" WHEN 3 THEN "CIERRE" WHEN 4 THEN "APORTE CAPITAL" END) AS tipomovimientodescripcion, #10
					cm.fecha, #11
					cm.tipodocumento, #11
					IFNULL((SELECT sigla FROM tipodocumento WHERE codTipoDocumento = cm.tipodocumento),"") AS documento, #12
					cm.codSerie, #13
					cm.serie, #14
					CONCAT(IFNULL((SELECT sigla FROM tipodocumento WHERE codTipoDocumento = cm.tipodocumento),"")," ",cm.serie, " - ",LPAD(cm.codPago,9,0)) AS NumDocumento, #15
					(CASE cm.tipo
					WHEN 1 THEN IFNULL((SELECT CONCAT((SELECT t.sigla FROM tipodocumento t WHERE t.codTipoDocumento = ns.codTipoDocumento), " ", ns.serie, " - ", LPAD(ns.numDocumento,7,0)) 
											FROM pago p 
											INNER JOIN factura_venta ns on ns.codFacturaV = p.codNota 
											WHERE p.codPago = cm.codPago),(
											SELECT CONCAT((SELECT t.sigla FROM tipodocumento t WHERE t.codTipoDocumento = p.codTipoDocumento ), " ", p.serie, " - ", LPAD(p.numdocumento,7,0)) 
											FROM pago p 											
											WHERE p.codPago = cm.codPago))

					WHEN 2 THEN IFNULL((SELECT ni.DocumentoFactura 
											FROM pago p 
											INNER JOIN facturacion ni on ni.codFactura = p.codNota # cambiar
											WHERE p.codPago = cm.codPago),"")
					END) AS documentorefencia, #15
					cm.toneladas, #16
					cm.codTipoPagoCaja, #17
					IFNULL((CASE cm.codTipoPagoCaja WHEN 5 THEN "EFECTIVO" WHEN 6 THEN "DEPOSITO" WHEN 7 THEN "CHEQUE" WHEN 8 THEN "TARJETA" WHEN 9 THEN "TRANSFERENCIA" END),"") AS PAGOCAJA, #18
					0 AS SALDO, #19
					cm.estado, #20
					cm.codUser, #21
					cm.fecharegistro, #22
					cm.codMoneda,
					(CASE cm.codMoneda WHEN 1 THEN "SOLES" WHEN 2 THEN "DOLARES" END) AS moneda,
					cm.tcVenta
	FROM 		caja c
	INNER JOIN cajamovimiento cm ON cm.codcaja = c.codcaja
	WHERE 	c.codsucursal = codSucur 
	AND 		DATE(c.fechaapertura) = DATE(fecha1)
	AND 		c.codcaja = codcaja_ex
	AND			c.codalmacen=codalma
  AND     cm.estado=1;

END
;;
DELIMITER ;

