DROP PROCEDURE IF EXISTS `ListaCajaChicaFechas`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaCajaChicaFechas`(codSucur int(11), fecha1 date, fecha2 date)
BEGIN
#Routine body goes here...
SELECT 	c.codCajaChica, CONCAT("C",LPAD(c.codCajaChica,7,0)) codPersonalizado, c.concepto, 	c.fecha,   IFNULL(c.razonSocial,'') as razonSocial,IFNULL(c.tipodocumento,'') as tipodocumento,
				c.NumDocumento, c.numGuia, c.numRecLiquidacion, IFNULL(t.codTipoPagoCaja,0) as codTipoPagoCaja,
				t.descripcion as TipoPagoCaja, (CASE cargadescarga WHEN 1 THEN 'CARGA' WHEN 2 THEN 'DESCARGA' 
				ELSE '' END) as cargadescarga, (CASE c.tipo WHEN 1 then 'INGRESO' WHEN 2 THEN 'EGRESO' END) as 
				TipoMovimiento, IF(C.toneladas>0,C.toneladas, NULL) AS toneladas, c.monto, c.comentario, apertura ,
				IFNULL(c.codRecibo,0) as codRecibo, 
				IFNULL(CONCAT(ser.serie,' - ',rec.numeracion),'') as recibo ,  IFNULL(rec.montopendiente,0)as montopendiente,c.statusRendicion
				

FROM cajachica as c 
LEFT JOIN tipopagocaja as t ON t.codTipoPagoCaja = c.codTipoPagoCaja
LEFT JOIN recibos rec ON rec.codRecibo=c.codRecibo
LEFT JOIN serie ser ON ser.codSerie=rec.codSerie
WHERE c.codSucursal = codSucur AND c.estado = 1 and (statusRendicion = 0 OR statusRendicion = 3)AND 
(apertura = 0 OR apertura = 2) and c.fecha BETWEEN DATE(fecha1) and DATE(fecha2)

ORDER BY c.codCajaChica;

END
;;
DELIMITER ;

