DROP PROCEDURE IF EXISTS `ListaCotizaciones`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaCotizaciones`(codalma int(11))
BEGIN

    SELECT LPAD(ct.codCotizacion,11,'0') as codCotizacion, ct.fechacotizacion as fecha, cl.razonsocial as cliente,
									ct.total, CONCAT(u.nombre," ",u.apellido) as responsable, ct.fechavigencia, 
							 ct.codTipoDocumento AS documento
		FROM cotizacion ct 	INNER JOIN cliente cl on cl.codCliente = ct.codCliente
						 INNER JOIN usuario u on u.codUsuario = ct.codUsuario INNER JOIN almacen al ON al.codAlmacen=ct.codAlmacen
						 INNER JOIN detallecotizacion dt ON dt.codCotizacion=ct.codCotizacion
		WHERE ct.codAlmacen = codalma AND ct.estado = 1  AND ct.anulado = 0 AND ct.pendiente=1 
          AND dt.cantidadpendiente > 0 AND ct.vigente >= 1
		GROUP BY ct.codCotizacion
		ORDER BY ct.codCotizacion;
    
END
;;
DELIMITER ;

