DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaDocumentosGuias`(alm int(11), codcliente int(11), tipo int(1))
BEGIN

IF tipo = 1 THEN 

		SELECT DISTINCT fv.codFacturaV as codigo, (SELECT nombre FROM almacen al1 WHERE al1.codAlmacen=fv.codAlmacen) as codAlmacen, 
           DATE(fv.fechasalida)as fecha, td.sigla as sigla, s.serie, LPAD(fv.numDocumento,6,0)as numdoc
		FROM factura_venta fv INNER JOIN tipodocumento td on fv.codTipoDocumento = td.codTipoDocumento
         LEFT OUTER JOIN serie s ON s.codSerie=fv.codSerie
         LEFT OUTER JOIN cliente cl ON cl.codCliente=fv.codCliente
         LEFT OUTER JOIN detallefactura_venta dt ON dt.codFacturaV = fv.codFacturaV

		WHERE fv.estado = 1 AND fv.anulado = 0 AND fv.entregado = 0 /*AND nt.documentoreferencia IS NULL */
					AND fv.codTransaccion = 7 AND fv.codAlmacen =alm AND s.preimpreso = 0 AND fv.codCliente = codcliente 
          AND dt.cantidadpendiente > 0 /*el codigo transaccion 7 es para salida por venta;*/

		UNION

		SELECT DISTINCT fv2.codFacturaV as codigo,(SELECT nombre FROM almacen al1 WHERE al1.codAlmacen=fv2.codAlmacen) as codAlmacen, 
           DATE(fv2.fechasalida )as fecha, td.sigla as sigla, s.serie, LPAD(fv2.numDocumento,6,0)as numdoc
