         LEFT OUTER JOIN cliente cl ON cl.codCliente=fv2.codCliente
         LEFT OUTER JOIN detallefactura_venta dt ON dt.codFacturaV = fv2.codFacturaV
    WHERE fv2.estado = 1 AND fv2.anulado = 0 AND fv2.entregado = 0 /*AND nt.documentoreferencia IS NULL */
					AND fv2.codTransaccion = 7 AND fv2.activarVista = 1 AND s.preimpreso = 0 AND fv2.codCliente=codcliente
          AND dt.cantidadpendiente > 0
    ORDER BY 2 DESC;

ELSEIF tipo = 2 THEN

    SELECT fv.codFacturaV as codigo, (SELECT nombre FROM almacen al1 WHERE al1.codAlmacen=fv.codAlmacen) as codAlmacen, DATE(fv.fechasalida)as fecha, td.sigla as sigla, s.serie,
           LPAD(fv.numDocumento,6,0)as numdoc
		FROM factura_venta fv INNER JOIN tipodocumento td on fv.codTipoDocumento = td.codTipoDocumento
         LEFT OUTER JOIN serie s ON s.codSerie=fv.codSerie
         LEFT OUTER JOIN cliente cl ON cl.codCliente=fv.codCliente
         LEFT OUTER JOIN detallefactura_venta dt ON dt.codFacturaV = fv.codFacturaV
		WHERE fv.estado = 1 AND fv.anulado = 0 AND fv.entregado = 0 /*AND nt.documentoreferencia IS NULL */
					AND fv.codTransaccion = 7 AND fv.codAlmacen = alm AND s.preimpreso = 1 AND fv.codCliente = codcliente
          AND dt.cantidadpendiente > 0 /*el codigo transaccion 7 es para salida por venta;*/

		UNION

		SELECT fv2.codFacturaV as codigo,(SELECT nombre FROM almacen al1 WHERE al1.codAlmacen=fv2.codAlmacen) as codAlmacen, DATE(fv2.fechasalida )as fecha, td.sigla as sigla, s.serie, 
           LPAD(fv2.numDocumento,6,0)as numdoc
		FROM factura_venta fv2 INNER JOIN tipodocumento td on fv2.codTipoDocumento = td.codTipoDocumento
		     LEFT OUTER JOIN serie s ON s.codSerie=fv2.codSerie
         LEFT OUTER JOIN cliente cl ON cl.codCliente=fv2.codCliente
         LEFT OUTER JOIN detallefactura_venta dt ON dt.codFacturaV = fv2.codFacturaV
    WHERE fv2.estado = 1 AND fv2.anulado = 0 AND fv2.entregado = 0 /*AND nt.documentoreferencia IS NULL */
					AND fv2.codTransaccion = 7 AND fv2.activarVista = 1 AND s.preimpreso = 1 AND fv2.codCliente=codcliente
          AND dt.cantidadpendiente > 0 
    ORDER BY 2 DESC;

END IF;


