		       AND nt.codFacturaV NOT IN (SELECT DISTINCT (codReferencia) FROM notaingreso WHERE codTransaccion=20 and codReferencia=nt.codFacturaV) AND
					nt.codFacturaV NOT IN (SELECT DISTINCT (codAplicada) FROM notaingreso WHERE codTransaccion = 20 and codAplicada = nt.codFacturaV)
					AND  ISNULL(nt.codNotaCredito)
		ORDER BY nt.fechasalida DESC, nt.codFacturaV DESC;
ELSE
		SELECT  nt.codFacturaV as codigo ,DATE( nt.fechasalida ) as fecha , td.sigla as sigla, CONCAT(nt.serie,' - ',LPAD(nt.numDocumento,6,0)) as numdoc
		FROM factura_venta nt
		LEFT OUTER JOIN tipodocumento td on nt.codTipoDocumento= td.codTipoDocumento
		WHERE 	nt.codCliente = codcli AND nt.anulado = 0 AND nt.estado = 1 AND (td.codTipoDocumento = 1 OR td.codTipoDocumento = 2) AND  nt.codTransaccion = 7 #el codigo transaccion 7 es para salida por venta
		       AND nt.codFacturaV NOT IN (SELECT codFacturaV FROM factura_venta WHERE codTransaccion=21 and codFacturaV=nt.codFacturaV)
		ORDER BY nt.fechasalida DESC, nt.codFacturaV DESC;
END IF;



END
