INNER JOIN usuario u on u.codUsuario = fac.codUsuario
LEFT JOIN tipodocumento tipdoc ON tipdoc.codTipoDocumento=fac.codTipoDocumento
LEFT OUTER JOIN proveedor pr on fac.codProveedor = pr.codProveedor
INNER JOIN moneda mon ON mon.codMoneda=fac.moneda
INNER JOIN metodopago pag ON pag.codMetodoPago=fac.formapago
#INNER JOIN tipocambio tipcam ON tipcam.codMoneda=fac.tipocambio
WHERE date(fac.fechaingreso) BETWEEN fechaini AND fechafin and fac.codalmacen=alma
