-- ----------------------------
-- Procedure structure for ListaGuiaRemisiones
-- ----------------------------
DROP PROCEDURE IF EXISTS `ListaGuiaRemisiones`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaGuiaRemisiones`(codalma int(11))
BEGIN

SELECT LPAD(gr.codGuiaRemision,6,'0') as codGuiaRemision, CONCAT(td.sigla,' - ',s.serie,' - ',gr.numdocumento) AS numdoc,

	IF(IFNULL(cl.codCliente,0)!=0,
		IFNULL((SELECT CONCAT(t.sigla,' - ',se.serie,' - ',f.numDocumento)
		FROM tipodocumento t INNER JOIN factura_venta f ON t.codTipoDocumento=f.codTipoDocumento 
		INNER JOIN serie se ON f.codSerie=se.codSerie
		WHERE f.codFacturaV=gr.codFactura),

		(SELECT CONCAT(t.sigla,' - ',se.serie,' - ',f.codTransDir)
			FROM tipodocumento t INNER JOIN transferencia f ON t.codTipoDocumento=f.codTipoDocumento 
			INNER JOIN serie se ON f.codserie=se.codSerie
			WHERE f.codTransDir = gr.codTransferencia)

),
		IF(IFNULL(gr.codProveedor,0)!=0,
			(SELECT CONCAT(t.sigla,' - ',f.numdocumento)
			FROM tipodocumento t INNER JOIN notaingreso f ON t.codTipoDocumento=f.codTipoDocumento 
			WHERE f.codNotaIngreso=gr.codFactura),
			(SELECT CONCAT(t.sigla,' - ',se.serie,' - ',f.numdocumento)
			FROM tipodocumento t INNER JOIN notasalida f ON t.codTipoDocumento=f.codTipoDocumento 
			INNER JOIN serie se ON f.codSerie=se.codSerie
			WHERE f.codNotaSalida=gr.codFactura AND f.codTransaccion=15)))as factura,
 
		DATE_FORMAT(gr.fechatraslado,'%d-%m-%Y')as fecha,

	IF(IFNULL(cl.codCliente,0)!=0,cl.razonsocial,
		IF(IFNULL(gr.codProveedor,0)!=0,
			(SELECT pv.razonsocial FROM proveedor pv WHERE gr.codProveedor=pv.codProveedor),
			(SELECT al.nombre FROM almacen al WHERE al.codAlmacen = (SELECT n.codalmacenreceptor FROM notasalida n 
			WHERE n.codNotaSalida=gr.codFactura))))as cliente, 

		CONCAT(u.nombre," ",u.apellido) as responsable, DATE_FORMAT(gr.fechaemision,'%d-%m-%Y')as fechaemision, gr.facturado, 
		(CASE gr.facturado WHEN 1 THEN "FACTURADO" WHEN 0 THEN "SIN FACTURAR" END)AS estadofacturado, gr.codMotivo, 
		gr.numdocumento AS numeroguia
FROM guiaremision gr INNER JOIN usuario u ON gr.codUser=u.codUsuario
INNER JOIN serie s ON s.codSerie=gr.codserie
INNER JOIN tipodocumento td ON td.codTipoDocumento=gr.codDocumento
LEFT OUTER JOIN cliente cl ON cl.codCliente=gr.codCliente
WHERE gr.estado=1 AND gr.codAlmacen=codalma
ORDER BY gr.codGuiaRemision desc;
END
;;
DELIMITER ;

