DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaMovimientosDesactivos`(codbanco_ex int(11),codcuenta_ex int(11),  codalma int(11))
BEGIN

SELECT cm.codCtaCteMovimiento, b.codBanco, b.descripcion as NomBanco, cc.codCuentaCorriente, 
       cc.cuentaCorriente as cuentacorriente, cm.NumTransaccion ,
       cm.fechaMovimiento, IF(cm.egreso>0,"EGRESO","INGRESO") as Transaccion, cm.egreso, cm.ingreso, cm.saldo,
       cm.descripcion,cm.tcventa,cm.tccompra, cm.nombre, cm.direccion, cm.dni, t.descripcion AS desctipomovimiento,
			 cm.activo,IF(cm.activo=1,'COBRADO','NO COBRADO') AS descripcionactivo, 
			(SELECT IFNULL(IFNULL(SUM(ct.ingreso),0) - IFNULL(SUM(ct.egreso),0) ,0)
			 FROM ctactemovimientos ct 
			 WHERE ct.codBanco = codbanco_ex 
			 AND ct.codCuentaCorriente = codcuenta_ex
