FROM ctactemovimientos cm 
INNER JOIN ctacte cc ON cm.codCuentaCorriente = cc.codCuentaCorriente
INNER JOIN banco b ON cm.codBanco = b.codbanco
LEFT OUTER JOIN tipopagocaja t ON t.codTipoPagoCaja = cm.codtipopagoservicio
