DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaNotaDebitoCompra`(codalma int(11), fecha1 datetime, fecha2 datetime)
BEGIN
	SELECT 
		LPAD(fc1.codFactura,11,'0') as codNotaSalida, fc1.DocumentoFactura as numdoc, fc1.fechaingreso as fecha,
		pr.codProveedor, pr.razonsocial, fc1.codReferencia as codReferencia, fc.DocumentoFactura as numerorefencia,

IFNULL((SELECT codNotaIngreso FROM notaingreso ni INNER JOIN tipodocumento td1 ON ni.codTipoDocumento = td1.codTipoDocumento
WHERE ni.codTipoDocumento = 2 AND fc.DocumentoFactura = CONCAT(td1.sigla,'-',ni.numdocumento)),0) AS codNotaIngreso,

		(SELECT CASE fc1.anulado WHEN 0 THEN "ACTIVO" WHEN 1 THEN "ANULADO" END) AS anulado, CONCAT(u.nombre,' ',u.apellido) as responsable, fc1.total as Total
	FROM 
		facturacion fc1 INNER JOIN usuario u ON u.codUsuario = fc1.codUsuario
		INNER JOIN facturacion fc ON fc.codFactura = fc1.codReferencia
		INNER JOIN tipodocumento td ON td.codTipoDocumento = fc1.codTipoDocumento
		INNER JOIN proveedor pr ON fc1.codProveedor = pr.codProveedor
		WHERE fc1.codalmacen = codalma AND fc1.estado = 1 AND fc1.codTransaccion = 18 AND DATE(fc1.fechaingreso) BETWEEN DATE(fecha1) AND DATE(fecha2)
	ORDER BY fc1.codFactura desc;
END
;;
DELIMITER ;

