DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaNotasCredito`(codalma int(11), fecha1 datetime, fecha2 datetime)
BEGIN
#Routine body goes here...
SELECT 	LPAD(nt.codNotaCredito,11,'0') as codNotaCredito, CONCAT(tdd.sigla, ' - ',nt.serie,' - ',SUBSTR(nt.DocumentoFactura,4)) as numdoc, 
				nt.fechaingreso as fecha, cl.razonsocial as cliente, nt.codReferencia, CONCAT(td.sigla, ' - ', fv.serie,' - ', 
				fv.numDocumento) as docref, ve.nombre as vendedor, (SELECT CASE nt.anulado WHEN 0 THEN "ACTIVO" WHEN 1 THEN 
				"ANULADO" END) AS anulado, CONCAT(u.nombre," ",u.apellido) as responsable, nt.total as Total, 
				CASE WHEN nt.cancelado = 0 THEN (SELECT CASE nt.codmovimientonc WHEN	0 THEN "DEVOLUCION DE DINERO" WHEN 1 THEN "SALDO A FAVOR" END)
						 WHEN nt.cancelado = 1 THEN (SELECT CASE nt.codmovimientonc WHEN	0 THEN "SE DEVOLVIO EL DINERO" WHEN 1 THEN "SE UTILIZO LA NOTA CREDITO" END)	END AS operacion
, cl.codCliente as codcliente

FROM notacredito nt	
INNER JOIN usuario u ON u.codUsuario = nt.codUsuario
INNER JOIN tipodocumento tdd ON tdd.codTipoDocumento = nt.codTipoDocumento

INNER JOIN cliente cl ON cl.codCliente = nt.codCliente
INNER JOIN notaingreso nin ON nin.codNotaIngreso = nt.codNotaI
INNER JOIN factura_venta fv ON fv.codFacturaV = nin.codReferencia
INNER JOIN tipodocumento td ON td.codTipoDocumento = fv.codTipoDocumento
LEFT JOIN vendedor ve ON ve.codVendedor = fv.codVendedor

WHERE nt.codAlmacen = codalma AND nt.estado = 1 AND nt.codTransaccion = 20 AND DATE(nt.fechaingreso) BETWEEN DATE(fecha1) AND DATE(fecha2)
ORDER BY nt.codNotaCredito desc;

END
;;
DELIMITER ;

