	 INNER JOIN facturacion fc ON fc.codFactura = ns1.documentoreferencia
	INNER JOIN tipodocumento td ON td.codTipoDocumento = ns.codTipoDocumento
	INNER JOIN proveedor pr ON ns.codProveedor=pr.codProveedor
WHERE 
	ns.codAlmacen = codalma AND 
	ns.estado = 1 AND 
	ns.codTransaccion = 19  AND DATE(ns.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2) AND
	(ns.motivo = 'Devolucion Mercaderia' OR ns.motivo = 'Anulacion')	
ORDER BY ns.codNotaSalida desc)

UNION

(SELECT 
	LPAD(ns.codNotaSalida,11,'0') as codNotaSalida, 
	CONCAT(td.sigla, '-',ns.serie,'-',ns.numdocumento) as numdoc, 
	ns.fechasalida as fecha,
	pr.codProveedor, 
	pr.razonsocial, 
	ns.documentoreferencia as codReferencia, 
	fc.DocumentoFactura as numerorefencia,
	IFNULL((SELECT codNotaIngreso FROM notaingreso ni INNER JOIN tipodocumento td1 ON ni.codTipoDocumento = td1.codTipoDocumento
	WHERE ni.codTipoDocumento = 2 AND fc.DocumentoFactura = CONCAT(td1.sigla,'-',ni.numdocumento)),0) AS codNotaIngreso,
	(SELECT CASE ns.anulado WHEN 0 THEN "ACTIVO" WHEN 1 THEN "ANULADO" END) AS anulado, CONCAT(u.nombre,' ',u.apellido) as responsable, ns.total as Total
FROM 
	notasalida ns INNER JOIN usuario u ON u.codUsuario = ns.codUsuario
	INNER JOIN facturacion fc ON fc.codFactura = ns.documentoreferencia
	INNER JOIN tipodocumento td ON td.codTipoDocumento = ns.codTipoDocumento
	INNER JOIN proveedor pr ON ns.codProveedor=pr.codProveedor
WHERE 
	ns.codAlmacen = codalma AND 
	ns.estado = 1 AND 
	ns.codTransaccion = 19   AND  DATE(ns.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2) AND
 (ns.motivo = 'Ajuste diferencia Precio' OR ns.motivo = 'Bonificacion')
ORDER BY ns.codNotaSalida desc);
END
;;
DELIMITER ;

