FROM 			producto p 	INNER JOIN unidadmedida u ON p.codUnidadMedida = u.codUnidadMedida
LEFT OUTER JOIN productoalmacen pa on pa.codProducto=p.codProducto
INNER JOIN controlstock cs ON p.codControlStock = cs.codControlStock
LEFT JOIN marca mr ON p.codMarca = mr.codMarca
LEFT JOIN grupo gr ON gr.codGrupo = p.codGrupo
WHERE 		p.codFamilia = codigo AND pa.codAlmacen = codalm 
ORDER BY 	p.descripcion;
ELSEIF nivel = 1 THEN
SELECT 		p.codProducto, p.referencia, p.descripcion, mr.descripcion AS nmarca,
					IFNULL((SELECT pro1.razonsocial from productosproveedor pp1 INNER JOIN proveedor pro1 on pro1.codProveedor=pp1.codProveedor WHERE pp1.codProducto=p.codProducto LIMIT 1),'') as Proveedor
					,u.descripcion as unidad, cs.descripcion as control, pa.stockactual,(pa.stockactual-pa.stockdisponible) as pedido
					,pa.valorpromedio,pa.valorpromediosoles as valorpromsoles, (pa.valorpromedio * pa.stockactual) as valorizado,pa.ultprecio, IFNULL(pa.stockminimo,0) as smin, IFNULL(pa.stockmaximo,0) as smax, IFNULL( pa.stockreposicion,0) as srepo, gr.descripcion as modelo
FROM 			producto p 	INNER JOIN unidadmedida u ON p.codUnidadMedida = u.codUnidadMedida
INNER JOIN controlstock cs ON p.codControlStock = cs.codControlStock
LEFT JOIN marca mr ON p.codMarca = mr.codMarca
LEFT JOIN grupo gr ON gr.codGrupo = p.codGrupo
LEFT OUTER JOIN productoalmacen pa ON p.codProducto = pa.codProducto
WHERE 		p.codLinea = codigo AND pa.codAlmacen = codalm 
ORDER BY 	p.descripcion;
ELSEIF nivel = 2 THEN
SELECT 		p.codProducto, p.referencia, p.descripcion,mr.descripcion AS nmarca,
					IFNULL((SELECT pro1.razonsocial from productosproveedor pp1 INNER JOIN proveedor pro1 on pro1.codProveedor=pp1.codProveedor WHERE pp1.codProducto=p.codProducto LIMIT 1),'') as Proveedor			
