BEGIN
#Routine body goes here...
SELECT 	c.codCajaChica, CONCAT("C",LPAD(c.codCajaChica,7,0)) codPersonalizado, c.concepto, 	c.fecha, 
				c.NumDocumento, c.numGuia, c.numRecLiquidacion, IFNULL(t.codTipoPagoCaja,0) as codTipoPagoCaja,
				t.descripcion as TipoPagoCaja, (CASE cargadescarga WHEN 1 THEN 'CARGA' WHEN 2 THEN 'DESCARGA' 
				ELSE '' END) as cargadescarga, (CASE c.tipo WHEN 1 then 'INGRESO' WHEN 2 THEN 'EGRESO' END) as 
				TipoMovimiento, IF(c.toneladas>0,c.toneladas, NULL) AS toneladas, c.monto, comentario

FROM cajachica as c LEFT JOIN tipopagocaja as t ON t.codTipoPagoCaja = c.codTipoPagoCaja

WHERE c.codSucursal = codSucur AND c.estado = 1 and c.statusRendicion = 1 AND c.apertura = 0  AND 
			c.codCajaChica not in(SELECT d.codCajaChica FROM detallerendicion d WHERE d.estatus=2) AND c.tipocajachica=tipo

ORDER BY c.codCajaChica;

END
;;
