BEGIN
DECLARE fechamax date;
DECLARE fechamin date;

(SELECT MAX(DATE(ch.fecha))INTO fechamax
						  FROM cajachica as ch LEFT JOIN tipopagocaja as t ON t.codTipoPagoCaja = ch.codTipoPagoCaja 
						  INNER JOIN detallerendicion dr ON dr.codCajaChica=ch.codCajaChica
