              WHERE ch.codSucursal = codSucur AND ch.estado = 1 and ch.statusRendicion = 1 AND ch.apertura = 0 AND ch.tipocajachica=tipo AND 
			        ch.codCajaChica not in(SELECT d.codCajaChica FROM detallerendicion d WHERE d.estatus=2 ));

(SELECT MIN(DATE(ch.fecha))INTO fechamin
						  FROM cajachica as ch LEFT JOIN tipopagocaja as t ON t.codTipoPagoCaja = ch.codTipoPagoCaja 
						  INNER JOIN detallerendicion dr ON dr.codCajaChica=ch.codCajaChica
						  INNER JOIN rendicion re ON re.codRendicion=dr.codRendicion
              WHERE ch.codSucursal = codSucur AND ch.estado = 1 and ch.statusRendicion = 1 AND ch.apertura = 0 AND ch.tipocajachica=tipo AND 
			        ch.codCajaChica not in(SELECT d.codCajaChica FROM detallerendicion d WHERE d.estatus=2));

SELECT  c.codCajaChica,c.fecha,IFNULL(c.razonSocial,'')as razonSocial,IFNULL(c.tipodocumento,'') as tipodocumento,IFNULL(c.NumDocumento,'') as NumDocumento,
					CONCAT(IFNULL(c.tipodocumento,'') ,' - ',IFNULL(c.NumDocumento,'')) as Documento, c.monto ,
					IFNULL((SELECT ch.monto 
						  FROM cajachica as ch LEFT JOIN tipopagocaja as t ON t.codTipoPagoCaja = ch.codTipoPagoCaja 
						  INNER JOIN detallerendicion dr ON dr.codCajaChica=ch.codCajaChica
						  INNER JOIN rendicion re ON re.codRendicion=dr.codRendicion
              WHERE ch.codSucursal = codSucur AND ch.tipocajachica=tipo AND ch.estado = 1 and ch.statusRendicion = 1 AND ch.apertura = 0 AND 
			        ch.codCajaChica not in(SELECT d.codCajaChica FROM detallerendicion d WHERE d.estatus=2)
						  AND ch.tipodocumento='FT' AND ch.codCajaChica=c.codCajaChica),0)  AS FT,
					IFNULL((SELECT ch.monto 
						  FROM cajachica as ch LEFT JOIN tipopagocaja as t ON t.codTipoPagoCaja = ch.codTipoPagoCaja 
						  INNER JOIN detallerendicion dr ON dr.codCajaChica=ch.codCajaChica
						  INNER JOIN rendicion re ON re.codRendicion=dr.codRendicion
              WHERE ch.codSucursal = codSucur AND ch.tipocajachica=tipo AND ch.estado = 1 and ch.statusRendicion = 1 AND ch.apertura = 0  AND 
			        ch.codCajaChica not in(SELECT d.codCajaChica FROM detallerendicion d WHERE d.estatus=2)
						  AND ch.tipodocumento='BV' AND ch.codCajaChica=c.codCajaChica),0)  AS BV,
					IFNULL((SELECT ch.monto 
						  FROM cajachica as ch LEFT JOIN tipopagocaja as t ON t.codTipoPagoCaja = ch.codTipoPagoCaja 
						  INNER JOIN detallerendicion dr ON dr.codCajaChica=ch.codCajaChica
						  INNER JOIN rendicion re ON re.codRendicion=dr.codRendicion
              WHERE ch.codSucursal = codSucur AND ch.tipocajachica=tipo AND ch.estado = 1 and ch.statusRendicion = 1 AND ch.apertura = 0  AND 
			        ch.codCajaChica not in(SELECT d.codCajaChica FROM detallerendicion d WHERE d.estatus=2)
						  AND ch.tipodocumento='RH' AND ch.codCajaChica=c.codCajaChica),0)  AS RH,
					IFNULL((SELECT ch.monto 
						  FROM cajachica as ch LEFT JOIN tipopagocaja as t ON t.codTipoPagoCaja = ch.codTipoPagoCaja 
						  INNER JOIN detallerendicion dr ON dr.codCajaChica=ch.codCajaChica
						  INNER JOIN rendicion re ON re.codRendicion=dr.codRendicion
              WHERE ch.codSucursal = codSucur AND ch.tipocajachica=tipo AND ch.estado = 1 and ch.statusRendicion = 1 AND ch.apertura = 0  AND 
			        ch.codCajaChica not in(SELECT d.codCajaChica FROM detallerendicion d WHERE d.estatus=2)
						  AND ch.tipodocumento='S/S' AND ch.codCajaChica=c.codCajaChica),0)  AS SS,
					CONCAT(t.descripcion,' - ', c.concepto)as concepto,IFNULL(vt.placa, '') as vehiculo,
				IFNULL(c.numGuia,'')as numGuia, IFNULL(c.numRecLiquidacion,'')as numRecLiquidacion , c.toneladas, 
        ach.montoApertura, ach.montoDisponible,re.numDocumento as CodRendicion,
				UPPER(s.nombre) as Sucursal,fechamax,fechamin

FROM cajachica as c LEFT JOIN tipopagocaja as t ON t.codTipoPagoCaja = c.codTipoPagoCaja 
LEFT JOIN vehiculotransporte vt ON vt.codVehiculoTransporte=c.codVehiculo
INNER JOIN aperturacajachica ach ON ach.codSucursal=c.codSucursal 
INNER JOIN detallerendicion dr ON dr.codCajaChica=c.codCajaChica
INNER JOIN rendicion re ON re.codRendicion=dr.codRendicion
INNER JOIN sucursal s on s.codSucursal=c.codSucursal
WHERE c.codSucursal = codSucur AND c.tipocajachica=tipo AND ach.tipo=tipo  AND c.estado = 1 and c.statusRendicion = 1 AND c.apertura = 0  AND
			c.codCajaChica not in(SELECT d.codCajaChica FROM detallerendicion d WHERE d.estatus=2 ) 

ORDER BY c.codCajaChica,c.tipodocumento;

END
;;
DELIMITER ;

