DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaTransacciones`(caso int(1))
BEGIN
#Routine body goes here...
IF caso = 2 THEN
	SELECT *, (SELECT case tipo when 0 then "INGRESO" when 1 then "SALIDA" END)as tipotext
	FROM transaccion
	WHERE estado = 1
	ORDER BY descripcion ;
ELSEIF caso = 0 THEN
	SELECT *
	FROM transaccion
	WHERE tipo = 0 AND estado = 1 AND codTransaccion=3 OR codTransaccion=4
	ORDER BY descripcion ;
ELSEIF caso = 1 THEN
	SELECT *
	FROM transaccion
	WHERE tipo = 1 AND estado = 1 AND codTransaccion=13
	ORDER BY descripcion ;
ELSEIF caso = 3 THEN
	SELECT * 
	FROM transaccion 
	WHERE tipo = 1 AND estado = 1 and descripcion not like '%directa'
	ORDER BY descripcion ;
END IF;
END
;;
DELIMITER ;

