DROP PROCEDURE IF EXISTS `ListaTransferenciasHistorial`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ListaTransferenciasHistorial`(codalma int(11))
BEGIN
SELECT ni.codNotaIngreso,s.codSerie as serie,ni.numdocumento,CONCAT(s.serie," - ",ni.numdocumento) as documento,
DATE(ni.fechaingreso) as fecha, CONCAT(u.nombre," ",u.apellido) as responsable,ni.comentario,ni.codAlmacen,
(SELECT CASE ni.codTransaccion
	when 15 then "DIRECTA" 
	when 17 Then "POR DEVOLUCION" 
END) as transferencia
FROM 		notaingreso ni
INNER JOIN usuario u on u.codUsuario = ni.codUsuario
inner join almacen al on al.codAlmacen=ni.codAlmacen
INNER JOIN tipodocumento td on td.codTipoDocumento=ni.codTipoDocumento
LEFT OUTER JOIN serie s on s.codSerie=ni.codSerie
WHERE ni.codAlmacen=codalma and  (ni.codTransaccion=15 or ni.codTransaccion=17) and ni.anulado=0
ORDER BY  CONCAT(s.serie," - ",ni.numdocumento);
END
;;
DELIMITER ;

