				, c.codigopersonalizado, c.razonsocial, IF(c.dni = "",c.ruc,c.dni)AS ruc_dni, "" as formapago,  lt.fechavencimiento as fechavence
				, IFNULL(DATE(lt.fechacancelado),' - ')as fechacancelado
				, IF(lt.cancelado = 0,IF(DATEDIFF(lt.fechavencimiento,DATE(NOW()))<0,DATEDIFF(lt.fechavencimiento,DATE(NOW())), ' - '),IF(DATEDIFF(lt.fechavencimiento,lt.fechacancelado)<0,DATEDIFF(lt.fechavencimiento,lt.fechacancelado),' - '))as diasmora
				,(SELECT CASE lt.codMoneda WHEN 1 THEN "SOLES" WHEN 2 THEN "DOLARES" END) AS moneda 
				,lt.monto as total, lt.montopendiente as pendiente, bc.descripcion as banco, lt.numerounico as num
				,(SELECT CASE esta WHEN 0 THEN "Ingresar Pago" WHEN 1 THEN "Muestra Pagos" END) AS accion
				,(SELECT COUNT(p.codPago) FROM pago p WHERE p.codNota = lt.codNota  AND p.estado = 1) as cantpagos

,(SELECT IFNULL(SUM(n.total),0)
	FROM factura_venta n LEFT JOIN formapago f	ON f.codFormaPago = n.formapago
	WHERE  /*n.codAlmacen = alma AND */ n.anulado = 0 AND n.estado = 1 AND n.cancelado = esta AND f.tipo = 1 AND n.codTransaccion = 7 AND DATE(fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS contado
,(SELECT IFNULL(SUM(n.total),0)
  FROM factura_venta n LEFT JOIN formapago f ON f.codFormaPago = n.formapago
  WHERE  /*n.codAlmacen = alma AND */ n.anulado = 0 AND n.estado = 1 AND n.cancelado = esta AND f.tipo = 0 AND n.codTransaccion = 7 AND DATE(n.fechasalida) BETWEEN DATE(fecha1) AND DATE(fecha2)) AS credito
	,(SELECT COUNT(p.codPago) FROM pago p WHERE p.codNota = nt.codFacturaV AND p.ingresoegreso = 1 AND p.estado = 1 AND p.Aprobado=1) as xaprobar
	FROM letra lt 			LEFT OUTER JOIN factura_venta nt ON nt.codFacturaV = lt.codNota
											LEFT OUTER JOIN cliente c ON lt.codLiberado = c.codCliente	
											LEFT OUTER JOIN zona zo ON zo.codZona = c.zona
											INNER JOIN almacen al ON lt.codAlmacen = al.codAlmacen
											LEFT OUTER JOIN tipodocumento td ON lt.codDocumento = td.codTipoDocumento
											LEFT OUTER JOIN tipodocumento ttdd ON nt.codTipoDocumento = ttdd.codTipoDocumento
											LEFT OUTER JOIN vendedor ve ON ve.codVendedor = nt.codVendedor
											LEFT OUTER JOIN banco bc ON lt.codBanco = bc.codBanco
	WHERE lt.cancelado = esta AND lt.estado = 1 AND nt.anulado = 0 /* AND al.codAlmacen = alma*/ AND date(lt.fechaemision) BETWEEN date(fecha1) AND date(fecha2)
														AND lt.ingresoegreso = 1 AND lt.codDocumento = codtipo
	ORDER BY fechavence DESC, codigo DESC;

END IF;
END
;;
DELIMITER ;

