DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraComisionesPorDocumentoFecha`(mes int(1), ao int(4))
BEGIN
	#Routine body goes here...
SELECT 	v.apellido, td.sigla, CONCAT(ns.serie,' - ',ns.numdocumento) as nro_documento, c.codigopersonalizado, c.razonsocial, z.descripcion as zona, ns.fechasalida
				, (ns.total - ns.igv) as totalns, IF(ns.anulado=0,'ACTIVO','ANULADO') as estado, IFNULL(CONCAT(ni.serie,' - ' , ni.numdocumento),"") as notaCredito
				, (ni.total - ni.igv) as totalni, ((ns.total - ns.igv) - (ni.total - ni.igv)) as base, IFNULL(SUM(dn.comision),0) as comison
FROM 		detallenotasalida dn
INNER JOIN notasalida ns ON dn.codNotaSalida=ns.codNotaSalida
INNER JOIN tipodocumento td ON ns.codTipoDocumento = td.codTipoDocumento
INNER JOIN cliente c ON ns.codCliente = c.codCliente
INNER JOIN vendedor v ON ns.codVendedor = v.codVendedor
LEFT OUTER JOIN zona z ON z.codZona = c.zona
LEFT OUTER JOIN notaingreso ni ON ni.codNotaIngreso = ns.codNotaCredito AND ni.anulado = 0 AND ni.estado = 1
WHERE 		ns.estado = 1 
					AND ns.codTransaccion = 7  
					AND ns.anulado = 0  
					AND MONTH(ns.fechasalida)= mes 
					AND YEAR (ns.fechasalida) = ao
					
GROUP BY ns.codNotaSalida ;
END
;;
DELIMITER ;

