DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraDetalleGuiaVenta`(codSalida int(11), codalma int(11))
BEGIN
  SELECT dt.codNotaSalida as codSalida, dt.codDetalleSalida as codDetalle, p.codProducto, p.referencia,p.descripcion AS producto, um.codUnidadMedida, um.descripcion AS unidad, 
         p.codControlStock as serielote, SUM(dt.cantidad) AS cantidad, dt.preciounitario, dt.subtotal, dt.descuento1,dt.descuento2,
         dt.descuento3,dt.montodscto as montodscto, (dt.importe-dt.igv) AS valorventa, dt.igv, dt.importe, 
         IFNULL((SELECT pa.stockdisponible FROM  productoalmacen pa 
										   WHERE p.codProducto=pa.codProducto AND pa.codAlmacen=codalma),0)  AS stockdisponible,
         dt.precioreal, dt.valoreal, dt.codUser, dt.fecharegistro
  FROM detallenotasalida dt LEFT OUTER JOIN producto p ON p.codProducto=dt.codProducto
			 LEFT OUTER JOIN unidadmedida um ON um.codUnidadMedida=dt.unidadingresada
       LEFT OUTER JOIN notasalida nt ON dt.codNotaSalida=nt.codNotaSalida
	WHERE dt.codNotaSalida=codSalida AND dt.codAlmacen=codalma AND dt.estado=1
	GROUP BY p.codProducto
  ORDER BY p.codProducto, dt.codNotaSalida;
END
;;
