CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraFacturaSalida`(codventa int(11), codalma int(11))
BEGIN
	SELECT dt.codFacturaV as codVenta, dt.codDetalleFacturaV as codDetalle, p.codProducto, p.referencia,p.descripcion AS producto, m.codMoneda,
				 m.descripcion as moneda,  um.codUnidadMedida, um.descripcion AS unidad, dt.serielote, SUM(dt.cantidadpendiente) AS cantidad, 
				 SUM(dt.cantidadpendiente) AS cantidadn, p.codControlStock, fp.codFormaPago, fp.descripcion AS FormaPago, ct.codListaPrecio as codLista, lp.nombre as ListaP,
				 dt.preciounitario, dt.subtotal, dt.descuento1,dt.descuento2,dt.descuento3,dt.montodscto,(dt.importe-dt.igv) AS valorventa, dt.igv, 
				 dt.importe, dt.precioreal, dt.valoreal,
				 IFNULL((SELECT pa.stockdisponible FROM  productoalmacen pa 
												 WHERE p.codProducto=pa.codProducto AND pa.codAlmacen=codalma),0)  AS stockdisponible, td.codTipoDocumento as codDocumento
	FROM detallefactura_venta dt LEFT OUTER JOIN producto p ON p.codProducto=dt.codProducto
			 LEFT OUTER JOIN unidadmedida um ON um.codUnidadMedida=dt.unidadingresada
       LEFT OUTER JOIN factura_venta ct ON dt.codFacturaV=ct.codFacturaV
       LEFT OUTER JOIN moneda m ON ct.moneda=m.codMoneda
       LEFT OUTER JOIN formapago fp ON fp.codFormaPago=ct.formapago
       LEFT OUTER JOIN listaprecio lp ON ct.codListaPrecio=lp.codListaPrecio
       LEFT OUTER JOIN tipodocumento td ON td.codTipoDocumento=ct.codTipoDocumento

	WHERE dt.codFacturaV=codventa AND dt.cantidadpendiente >0 AND dt.codAlmacen=codalma AND dt.estado=1
	GROUP BY p.codProducto
  ORDER BY dt.codDetalleFacturaV;
END
;;
DELIMITER ;

