DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraFacturasProveedor`(alma int(11), codpro int(11), tipo int(11))
BEGIN

/*SELECT 	fac.codFactura, fac.DocumentoFactura as documento ,IFNULL(pr.ruc,"") as ruc,
 IFNULL(pr.razonsocial,"") as razonsocial,IFNULL(pr.codProveedor,"")as codProveedor ,
fac.fechaingreso as fecha,
CONCAT(u.nombre," ",u.apellido) as responsable, fac.comentario,
(SELECT CASE fac.anulado when 0 then "Activo" when 1 Then "No Activo" END) as Estado
FROM 		facturacion fac
INNER JOIN usuario u on u.codUsuario = fac.codUsuario
LEFT OUTER JOIN proveedor pr on fac.codProveedor = pr.codProveedor 
LEFT OUTER JOIN notasalida ns ON fac.codFactura=ns.documentoreferencia 
WHERE fac.codalmacen=alma AND fac.codProveedor=codpro
and (ns.codTransaccion<>19 or ns.codTransaccion<>18)
ORDER BY fac.codFactura; */
-- alma int(11), codpro int(11)

/*  1 ==> NCC || 2 ==> NDC || 3 ==> NCC PARA SALIDA POR DEVOLUCION */

IF tipo = 1 THEN


SELECT 	fac.codFactura, fac.DocumentoFactura as documento ,IFNULL(pr.ruc,"") as ruc,
 IFNULL(pr.razonsocial,"") as razonsocial,IFNULL(pr.codProveedor,"")as codProveedor ,
fac.fechaingreso as fecha,
CONCAT(u.nombre," ",u.apellido) as responsable, fac.comentario,
(SELECT CASE fac.anulado when 0 then "Activo" when 1 Then "No Activo" END) as Estado
FROM 		facturacion fac
INNER JOIN usuario u on u.codUsuario = fac.codUsuario
LEFT OUTER JOIN proveedor pr on fac.codProveedor = pr.codProveedor 
-- LEFT OUTER JOIN notasalida ns ON fac.codFactura=ns.documentoreferencia 
WHERE fac.codalmacen=alma AND fac.codProveedor=codpro AND fac.codTransaccion<>18 AND fac.estado = 1
-- and (ns.codTransaccion<>19 or ns.codTransaccion<>18)
 AND fac.codFactura NOT IN (SELECT codAplicada FROM notasalida WHERE codTransaccion=19 and codAplicada=fac.codFactura) 
ORDER BY fac.codFactura;

ELSEIF tipo = 2 THEN

			SELECT 	fac.codFactura, fac.DocumentoFactura as documento ,IFNULL(pr.ruc,"") as ruc,
			 IFNULL(pr.razonsocial,"") as razonsocial,IFNULL(pr.codProveedor,"")as codProveedor ,
			fac.fechaingreso as fecha,
			CONCAT(u.nombre," ",u.apellido) as responsable, fac.comentario,
			(SELECT CASE fac.anulado when 0 then "Activo" when 1 Then "No Activo" END) as Estado
			FROM 		facturacion fac
			INNER JOIN usuario u on u.codUsuario = fac.codUsuario
			LEFT OUTER JOIN proveedor pr on fac.codProveedor = pr.codProveedor 
			-- LEFT OUTER JOIN notasalida ns ON fac.codFactura=ns.documentoreferencia 
			WHERE fac.codalmacen=alma AND fac.codProveedor=codpro AND fac.codTransaccion<>18 
			AND fac.codFactura NOT IN (SELECT codReferencia FROM facturacion WHERE codTransaccion = 18 AND codalmacen = alma)
			ORDER BY fac.codFactura;

ELSE

		SELECT 
				DISTINCT(fac.codFactura), 
				fac.DocumentoFactura as documento,
				IFNULL(pr.ruc,"") as ruc,
				IFNULL(pr.razonsocial,"") as razonsocial,
				IFNULL(pr.codProveedor,"")as codProveedor,
				fac.fechaingreso as fecha,
				CONCAT(u.nombre," ",u.apellido) as responsable, 
				fac.comentario,
				(SELECT CASE fac.anulado when 0 then "Activo" when 1 Then "No Activo" END) as Estado
		FROM
				notasalida ns, 
				detallenotasalida dns, 		
				facturacion fac, 
				detallefacturacion dfac,
				proveedor pr,
				usuario u
		WHERE 
			ns.codNotaSalida = dns.codNotaSalida AND 
			fac.codFactura = dfac.codFactura AND 
			ns.codProveedor = fac.codProveedor AND
			ns.codProveedor = pr.codProveedor AND
			fac.codUsuario = u.codUsuario AND
			fac.codalmacen = alma  AND
			ns.codProveedor = codpro AND
			ns.codTransaccion = 12 AND 
			fac.codTransaccion <> 18 AND
			ns.documentoreferencia = 0 AND 
			dns.codProducto = dfac.codProducto AND 
			DATE(ns.fecharegistro) BETWEEN DATE(DATE_SUB(ns.fecharegistro, INTERVAL 2 MONTH )) AND DATE(NOW());

END IF;

END
;;
DELIMITER ;

