DROP PROCEDURE IF EXISTS `MuestraGuias`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `MuestraGuias`(fecha1 date, fecha2 date)
BEGIN
#Routine body goes here...
SELECT LPAD(gr.codGuiaRemision,6,'0') as codGuiaRemision, CONCAT(IFNULL(tdd.sigla,""), ' - ',IFNULL(se.serie,""),' - ',IFNULL(gr.numdocumento,"")) as numdoc
			, CONCAT(td.sigla, ' - ',nt.serie,' - ',nt.numdocumento) as factura
			, gr.fechatraslado as fecha, cl.razonsocial as cliente
			, CONCAT(u.nombre," ",u.apellido) as responsable, gr.fechaemision, gr.facturado
			, (CASE gr.facturado WHEN 1 THEN "FACTURADO" WHEN 0 THEN "SIN FACTURAR" END) AS estadofacturado
FROM guiaremision gr 	
INNER JOIN cliente cl on cl.codCliente = gr.codCliente
INNER JOIN usuario u on u.codUsuario = gr.codUser
LEFT OUTER JOIN tipodocumento tdd ON tdd.codTipoDocumento = gr.codDocumento
LEFT OUTER JOIN serie se ON se.codSerie = gr.codserie
LEFT OUTER JOIN notasalida nt ON nt.codNotaSalida = gr.codFactura
LEFT OUTER JOIN tipodocumento td ON td.codTipoDocumento = nt.codTipoDocumento
WHERE DATE(gr.fechatraslado) BETWEEN DATE(fecha1) AND DATE(fecha2)  AND gr.estado = 1
ORDER BY gr.codGuiaRemision desc;
END
;;
DELIMITER ;

